CREATE OR REPLACE FUNCTION man_ordem_servico_pck.get_ds_classificacao ( nr_seq_ordem_serv_p man_ordem_servico.nr_sequencia%type, nr_seq_idioma_p idioma.nr_sequencia%type default 1) RETURNS varchar AS $body$
DECLARE

		
		ds_retorno_w	varchar(4000);
	
	
BEGIN
		
		select	substr(obter_desc_exp_idioma(vd.cd_exp_valor_dominio, nr_seq_idioma_p),1, 4000)
		into STRICT	ds_retorno_w
		from	valor_dominio vd
		where	vd.cd_dominio = 1149
		and	vd.vl_dominio =	(
						SELECT	max(mos.ie_classificacao)
						from	man_ordem_servico mos
						where	mos.nr_sequencia = nr_seq_ordem_serv_p
					);
	
	return ds_retorno_w;
	END;

$body$
LANGUAGE PLPGSQL
