CREATE OR REPLACE FUNCTION man_ordem_servico_pck.get_nr_seq_severidade_wheb ( nr_seq_ordem_serv_p man_ordem_servico.nr_sequencia%type, nr_seq_idioma_p idioma.nr_sequencia%type default 1) RETURNS varchar AS $body$
DECLARE

		
		ds_retorno_w	varchar(4000);
	
	
BEGIN
	
