CREATE OR REPLACE FUNCTION med_guidance_pkg.check_has_medical_pratice (nr_atendimento_p text, cd_medical_practice_code_p text) RETURNS boolean AS $body$
DECLARE

    qt_registros_w bigint;

    
BEGIN  
    
	SELECT COUNT(*) qt_registros
	INTO STRICT	qt_registros_w
	FROM	jpn_medical_guidance a,
		medical_guidance_order b      
