CREATE OR REPLACE FUNCTION med_guidance_pkg.check_has_narcotic_drugs (cd_pessoa_fisica_p PESSOA_FISICA.CD_PESSOA_FISICA%type) RETURNS boolean AS $body$
DECLARE


    qt_registros_w bigint;

    
BEGIN   
        SELECT	COUNT(*) qt_registros
        INTO STRICT	qt_registros_w
        FROM   	atendimento_paciente a
        INNER JOIN  material_atend_paciente b ON a.nr_atendimento = b.nr_atendimento
        INNER JOIN material c ON b.cd_material = c.cd_material and c.ie_receita = 'S'
        WHERE  	a.cd_pessoa_fisica = cd_pessoa_fisica_p;

     RETURN(qt_registros_w > 0);

    END;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
