CREATE OR REPLACE FUNCTION med_guidance_pkg.check_has_pacemaker (cd_pessoa_fisica_p PESSOA_FISICA.CD_PESSOA_FISICA%type) RETURNS boolean AS $body$
DECLARE


    qt_registros_w bigint;

    
BEGIN   
    
	SELECT 	COUNT(*)  qt_registros
	INTO STRICT   	qt_registros_w
	FROM 	cirurgia a
	INNER JOIN cir_marcapasso b ON a.nr_cirurgia = b.nr_cirurgia
	WHERE 	cd_pessoa_fisica = cd_pessoa_fisica_p
	AND 	coalesce(dt_inativacao::text, '') = '';

     RETURN(qt_registros_w > 0);

    END;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
