CREATE OR REPLACE FUNCTION med_guidance_pkg.convert_in_weeks (dt_start_p timestamp, dt_end_p timestamp) RETURNS bigint AS $body$
DECLARE


    qt_weeks_w  integer;


BEGIN
        SELECT  (TO_CHAR(dt_end_p, 'WW'))::numeric  - (TO_CHAR(dt_start_p, 'WW'))::numeric  +
                52 * ((TO_CHAR(dt_end_p, 'YYYY'))::numeric  - (TO_CHAR(dt_start_p, 'YYYY'))::numeric )
