CREATE OR REPLACE FUNCTION med_guidance_pkg.convert_period_days_pacient (si_period_p text, qt_tempo_p bigint) RETURNS bigint AS $body$
BEGIN
        
        CASE si_period_p
          WHEN 'D' THEN
            RETURN qt_tempo_p;
          WHEN 'M' THEN
            RETURN qt_tempo_p * 30;
          WHEN 'A' THEN
            RETURN qt_tempo_p * 365;
          ELSE
            RETURN 0;
        END CASE;

    END;


