CREATE OR REPLACE FUNCTION med_guidance_pkg.get_days_last_exam (cd_pessoa_fisica_p PESSOA_FISICA.CD_PESSOA_FISICA%type) RETURNS varchar AS $body$
DECLARE


    qt_dias_w varchar(255) := '0';

BEGIN	
		
	SELECT TRUNC(clock_timestamp() - b.dt_atualizacao) 
	INTO STRICT qt_dias_w
	FROM   procedimento_paciente a
	INNER  JOIN exame_laboratorio b ON a.nr_seq_exame = b.nr_seq_exame
	WHERE  a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	ORDER BY b.nr_seq_exame LIMIT 1;
		
     RETURN qt_dias_w;
				
    END;
	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
