SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION med_guidance_pkg.get_hospitalization_period (cd_pessoa_fisica_p PESSOA_FISICA.CD_PESSOA_FISICA%type, dt_last_guidance timestamp) RETURNS bigint AS $body$
DECLARE

    qt_registros_w bigint;

    
BEGIN  
    
	SELECT  COUNT(*)
	INTO STRICT    qt_registros_w
	FROM    atendimento_paciente
	WHERE   ie_tipo_atendimento = 1
	AND     cd_pessoa_fisica = cd_pessoa_fisica_p
	AND     DT_ENTRADA BETWEEN dt_last_guidance AND clock_timestamp();

     RETURN qt_registros_w;

    END;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
