CREATE OR REPLACE FUNCTION med_guidance_pkg.get_last_guidance (nr_atendimento_p text, cd_medical_practice_code_p text) RETURNS timestamp AS $body$
DECLARE

    dt_guidance_w timestamp;

    
BEGIN  
    
	SELECT 	b.dt_guidance
	INTO STRICT   	dt_guidance_w
	FROM   	jpn_medical_guidance a,
		medical_guidance_order b      
	WHERE  	a.nr_sequencia = b.nr_seq_jpn_med_guid
	AND    	b.nr_atendimento = nr_atendimento_p
	AND    	a.cd_medical_practice_code = cd_medical_practice_code_p 
	ORDER BY b.nr_seq_jpn_med_guid DESC LIMIT 1;

     RETURN dt_guidance_w;

    END;


