CREATE OR REPLACE FUNCTION med_guidance_pkg.get_role_med_assist (cd_pessoa_fisica_p PESSOA_FISICA.CD_PESSOA_FISICA%type) RETURNS varchar AS $body$
DECLARE


    cd_medico_atendimento_w varchar(255) := '0';
    cd_cargo_w varchar(255) := '0';

BEGIN	
		
	SELECT a.cd_medico_atendimento
	INTO STRICT cd_medico_atendimento_w
	FROM atendimento_paciente a
	WHERE a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	ORDER BY a.nr_atendimento LIMIT 1;
	
	SELECT obter_cargo_PF(cd_medico_atendimento_w, 'C')
	INTO STRICT cd_cargo_w
	;
		
     RETURN cd_cargo_w;
				
    END;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
