SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE medical_net_pck.send_edifact_laudo (nr_seq_laudo_p text, ie_medicalnet_id_p text) AS $body$
DECLARE

	ds_edifact_w	varchar(30000);
	ds_unb_w	varchar(300);
	ds_unh_w	varchar(300);
	ds_bgm_w	varchar(300);
	ds_ftx_w	varchar(1000);
	ds_nad_w	varchar(300);
	ds_unt_w	varchar(300);
	
	ds_sender_w	varchar(30);
	ds_receiver_w	varchar(30);
	dt_message_w	timestamp;
	
	ds_referenceNumber_w	varchar(30);
	
	ds_specialty_w		varchar(10);
	dt_laudo_w		timestamp;
	ds_socialSecurity_w	varchar(12);
	dt_birth_w		timestamp;
	
	ds_titulo_laudo_w	varchar(70);
	ds_laudo1_w		varchar(70);
	ds_laudo2_w		varchar(70);
	ds_laudo3_w		varchar(70);
	ds_laudo4_w		varchar(70);
	
	ds_surname_w		varchar(50);
	ds_givenName_w		varchar(50);
	ds_title_w		varchar(20);
	ds_street_w		varchar(120);
	ds_caseNumber_w		varchar(50);
	ds_city_w		varchar(120);
	ds_zipCode_w		varchar(20);
	count_segmentos_w	bigint;
	
	laudo_paciente_w		laudo_paciente%rowtype;
	procedimento_paciente_w		procedimento_paciente%rowtype;
	prescr_procedimento_w		prescr_procedimento%rowtype;
	pessoa_fisica_paciente_w	pessoa_fisica%rowtype;
	person_name_w			person_name%rowtype;
	medico_especialidade_w		medico_especialidade%rowtype;
	compl_pessoa_fisica_w		compl_pessoa_fisica%rowtype;
	episodio_paciente_w		episodio_paciente%rowtype;
	pessoa_titular_convenio_w	pessoa_titular_convenio%rowtype;
	log_mensagem_mednet_w		log_mensagem_mednet%rowtype;
	
	
BEGIN
	
	begin
	select	*
	into STRICT	laudo_paciente_w
	from	laudo_paciente
	where	nr_sequencia = nr_seq_laudo_p;
	exception
	when others then
		laudo_paciente_w	:=	null;
	end;
	
	begin
	select 	*
	into STRICT	procedimento_paciente_w
	from	procedimento_paciente
	where	nr_sequencia = laudo_paciente_w.nr_seq_proc;
	exception
	when others then
		procedimento_paciente_w	:=	null;
	end;
	
	begin
	select	*
	into STRICT	prescr_procedimento_w
	from	prescr_procedimento
	where	nr_prescricao = procedimento_paciente_w.nr_prescricao;
	exception
	when others then
		prescr_procedimento_w	:=	null;
	end;
	
	begin
	select	*
	into STRICT	pessoa_fisica_paciente_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = laudo_paciente_w.cd_pessoa_fisica;
	exception
	when others then
		pessoa_fisica_paciente_w	:=	null;
	end;
	
	begin
	select	*
	into STRICT	person_name_w
	from	person_name
	where	nr_sequencia = pessoa_fisica_paciente_w.nr_seq_person_name;
	exception
	when others then
		person_name_w	:=	null;
	end;
	
	begin
	select	*
	into STRICT	medico_especialidade_w
	from	medico_especialidade
	where	cd_pessoa_fisica = laudo_paciente_w.cd_medico_resp;
	exception
	when others then
		medico_especialidade_w	:=	null;
	end;
	
	begin
	select	*
	into STRICT	compl_pessoa_fisica_w
	from	compl_pessoa_fisica
	where	cd_pessoa_fisica = pessoa_fisica_paciente_w.cd_pessoa_fisica;
	exception
	when others then
		compl_pessoa_fisica_w	:=	null;
	end;
	
	begin
	select	*
	into STRICT	episodio_paciente_w
	from	episodio_paciente
	where	cd_pessoa_fisica = pessoa_fisica_paciente_w.cd_pessoa_fisica;
	exception
	when others then
		episodio_paciente_w	:=	null;
	end;
	
	begin
	select	*
	into STRICT 	pessoa_titular_convenio_w
	from 	pessoa_titular_convenio
	where	cd_pessoa_fisica = pessoa_fisica_paciente_w.cd_pessoa_fisica
	and	coalesce(dt_inicio_vigencia::text, '') = ''
	and	dt_validade_carteira < clock_timestamp();
	exception
	when others then
		pessoa_titular_convenio_w	:=	null;
	end;
	
	begin
	select	cd_externo
	into STRICT	ds_specialty_w
	from	conversao_meio_externo
	where	nm_tabela='MEDICO_ESPECIALIDADE'
	and	ie_sistema_externo = 'MNET' 
	and	cd_interno = to_char(medico_especialidade_w.cd_especialidade);
	exception
	when others then
		ds_specialty_w	:=	null;
	end;
	
	select	coalesce(get_personal_title(pessoa_fisica_paciente_w.nr_seq_forma_trat, 'A'),'')
	into STRICT	ds_title_w
	;
	
	ds_sender_w		:=	'Tasy';
	ds_receiver_w		:=	ie_medicalnet_id_p;
	dt_message_w		:=	clock_timestamp();
	ds_referenceNumber_w	:=	prescr_procedimento_w.nr_acesso_dicom;
	dt_laudo_w		:=	laudo_paciente_w.dt_laudo;
	ds_socialSecurity_w	:=	substr(pessoa_fisica_paciente_w.NR_INSS,1,4)|| to_char(pessoa_fisica_paciente_w.dt_nascimento, 'DDMMYYYY');
	dt_birth_w		:=	pessoa_fisica_paciente_w.dt_nascimento;
	ds_titulo_laudo_w	:=	laudo_paciente_w.ds_titulo_laudo;
	ds_laudo1_w		:=	substr(laudo_paciente_w.ds_laudo,1,70);
	ds_laudo2_w		:=	substr(laudo_paciente_w.ds_laudo,71,70);
	ds_laudo3_w		:=	substr(laudo_paciente_w.ds_laudo,141,70);
	ds_laudo4_w		:=	substr(laudo_paciente_w.ds_laudo,211,70);
	ds_surname_w		:=	person_name_w.ds_family_name;
	ds_givenName_w		:=	person_name_w.ds_given_name;
	ds_street_w		:=	compl_pessoa_fisica_w.ds_endereco;
	ds_caseNumber_w		:=	episodio_paciente_w.nr_episodio;
	ds_city_w		:=	compl_pessoa_fisica_w.ds_municipio;
	ds_zipCode_w		:=	compl_pessoa_fisica_w.cd_cep;

	count_segmentos_w	:=	6;
	
	ds_unb_w	:=	'UNB+ANSI:1+'||ds_sender_w||'+'||ds_receiver_w||'+'||TO_CHAR(dt_message_w, 'YYMMDD')||'+'||TO_CHAR(dt_message_w, 'HHmm')||'+1';
	ds_unh_w	:=	'UNH+'||ds_referenceNumber_w||'+MEDRPT:1:901:UN';
	ds_bgm_w	:=	'BGM+'||ds_specialty_w||'+++'||to_char(dt_laudo_w,'YYYYMMDD')||'++'||ds_socialSecurity_w||'+'||to_char(dt_birth_w, 'YYYYMMDD');
	ds_ftx_w	:=	'FTX+BFD++'||ds_titulo_laudo_w||':'||ds_laudo1_w||':'||ds_laudo2_w||':'||ds_laudo3_w||':'||ds_laudo4_w;
	ds_nad_w	:=	'NAD+PAT++'||ds_surname_w||':'||ds_givenName_w||':'||ds_title_w||':'||ds_street_w||':'||ds_caseNumber_w||'+++'||ds_city_w||'++'||ds_zipCode_w;
	ds_unt_w	:=	'UNT+'||count_segmentos_w||'+'||ds_referenceNumber_w;
	
	
	ds_edifact_w	:=	ds_unb_w||chr(39)||ds_unh_w||chr(39)||ds_bgm_w||chr(39)||ds_ftx_w||chr(39)||ds_nad_w||chr(39)||ds_unt_w||chr(39);
	
	log_mensagem_mednet_w.cd_convenio		:=	pessoa_titular_convenio_w.cd_convenio;
	log_mensagem_mednet_w.cd_pessoa_fisica		:=	pessoa_fisica_paciente_w.cd_pessoa_fisica;
	log_mensagem_mednet_w.ds_edifact		:=	ds_edifact_w;
	log_mensagem_mednet_w.ds_json			:=	edifact_json_converter(ds_edifact_w);
	log_mensagem_mednet_w.dt_atualizacao		:=	clock_timestamp();
	log_mensagem_mednet_w.dt_atualizacao_nrec	:=	clock_timestamp();
	log_mensagem_mednet_w.dt_nascimento		:=	dt_birth_w;
	log_mensagem_mednet_w.dt_resultado		:=	dt_laudo_w;
	log_mensagem_mednet_w.ie_envio_receb		:=	'E';
	log_mensagem_mednet_w.ie_medicalnet_dest	:=	ds_receiver_w;
	log_mensagem_mednet_w.ie_medicalnet_rem		:=	ds_sender_w;
	log_mensagem_mednet_w.ie_status			:=	'P';
	log_mensagem_mednet_w.nm_usuario		:=	'Tasy';
	log_mensagem_mednet_w.nm_usuario_nrec		:=	'Tasy';
	log_mensagem_mednet_w.nr_atendimento		:=	laudo_paciente_w.nr_atendimento;
	log_mensagem_mednet_w.nr_episodio		:=	ds_caseNumber_w;
	--log_mensagem_mednet_w.nr_seq_carta		:=	episodio_paciente_w.nr_seq_carta;
	log_mensagem_mednet_w.nr_seq_laudo		:=	nr_seq_laudo_p;

	select	nextval('log_mensagem_mednet_seq')
	into STRICT	log_mensagem_mednet_w.nr_sequencia
	;

	begin
	insert into log_mensagem_mednet values (log_mensagem_mednet_w.*);
	exception
	when others then
		log_mensagem_mednet_w	:=	null;
	end;
	commit;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE medical_net_pck.send_edifact_laudo (nr_seq_laudo_p text, ie_medicalnet_id_p text) FROM PUBLIC;
