CREATE OR REPLACE FUNCTION meta_cuidado_pck.getstatussingle ( nr_seq_meta_atend_p bigint) RETURNS varchar AS $body$
DECLARE

					
ie_status_w	varchar(10);

C001 CURSOR FOR
	SELECT	coalesce(a.ie_status,'I') ie_status
	from	tof_meta_atend a,
			tof_meta c 
	where	a.nr_seq_meta = c.nr_sequencia
	and		a.nr_sequencia = nr_seq_meta_atend_p
	and		c.ie_tipo_meta = 'U'
	and		Obter_se_meta_liberada(a.nr_atendimento,a.nr_seq_meta) = 'S'
	and     (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and     coalesce(a.dt_inativacao::text, '') = '';


BEGIN
open C001;
loop
fetch C001 into	
	ie_status_w;
EXIT WHEN NOT FOUND; /* apply on C001 */
end loop;
close C001;


return	coalesce(ie_status_w,'I');

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
