SET client_encoding TO 'UTF8';


	/*------------------------------------------------------------------------------------------------------------------------------------------------------------
	| CARREGAR OS DADOS DA TABELAHDM_AGENDAMENTO_ANEXO PARA W_HDM_AGENDAMENTO_ANEXO      |
	*/
CREATE OR REPLACE PROCEDURE mprev_agenda_pck.carregar_w_agendamento_anexo (nr_seq_agendamento_p bigint) AS $body$
DECLARE


	nr_seq_w_agendamento_w	w_mprev_agendamento.nr_sequencia%type;

	C01 CURSOR FOR
		SELECT	ds_arquivo,
				ds_observacao,
				ds_titulo,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				nm_usuario_nrec,
				nr_seq_agendamento
		from	hdm_agendamento_anexo
		where	nr_seq_agendamento = nr_seq_agendamento_p;

	
BEGIN

	/*Busca sequencia da tabela w_mprev_agendamento para inserir no campo nr_seq_w_agendamento*/

	select	max(nr_sequencia)
	into STRICT	nr_seq_w_agendamento_w
	from	w_mprev_agendamento
	where	nr_seq_agendamento = nr_seq_agendamento_p;

	/*Deleta os registros na tabela W*/

	delete	from w_hdm_agendamento_anexo
	where	nr_seq_agendamento = nr_seq_w_agendamento_w;

	for r_C01 in C01 loop

		insert into w_hdm_agendamento_anexo(nr_sequencia,
			ds_arquivo,
			ds_observacao,
			ds_titulo,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_agendamento)
		values (nextval('w_hdm_agendamento_anexo_seq'),
			r_C01.ds_arquivo,
			r_C01.ds_observacao,
			r_C01.ds_titulo,
			r_C01.dt_atualizacao,
			r_C01.dt_atualizacao_nrec,
			r_C01.nm_usuario,
			r_C01.nm_usuario_nrec,
			nr_seq_w_agendamento_w);

		commit;

	end loop;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
