SET client_encoding TO 'UTF8';


	/*---------------------------------------------------------------------------------------------------------------------------------------------
	|	CARREGAR OS DADOS DA TABELA MPREV_AGENDAMENTO PARA W_MPREV_AGENDAMENTO		|
	*/
CREATE OR REPLACE PROCEDURE mprev_agenda_pck.carregar_w_agendamento (nr_seq_agendamento_p bigint) AS $body$
DECLARE


	cd_agenda_w			w_mprev_agendamento.cd_agenda%type;
	cd_agenda_local_w         	w_mprev_agendamento.cd_agenda_local%type;
	cd_agenda_profissional_w	w_mprev_agendamento.cd_agenda_profissional%type;
	cd_estabelecimento_w     	w_mprev_agendamento.cd_estabelecimento%type;
	ds_observacao_w          	w_mprev_agendamento.ds_observacao%type;
	ds_utilizacao_w          	w_mprev_agendamento.ds_utilizacao%type;
	dt_agenda_w          		w_mprev_agendamento.dt_agenda%type;
	dt_atualizacao_w		w_mprev_agendamento.dt_atualizacao%type;
	dt_atualizacao_nrec_w		w_mprev_agendamento.dt_atualizacao_nrec%type;
	ie_forma_atendimento_w   	w_mprev_agendamento.ie_forma_atendimento%type;
	ie_previsto_w  			w_mprev_agendamento.ie_previsto%type;
	ie_tipo_agendamento_w		w_mprev_agendamento.ie_tipo_agendamento%type;
	ie_tipo_atendimento_w    	w_mprev_agendamento.ie_tipo_atendimento%type;
	nm_usuario_w    		w_mprev_agendamento.nm_usuario%type;
	nm_usuario_nrec_w		w_mprev_agendamento.nm_usuario_nrec%type;
	nr_minuto_duracao_w		w_mprev_agendamento.nr_minuto_duracao%type;
	nr_seq_ativ_extra_w     	w_mprev_agendamento.nr_seq_ativ_extra%type;
	nr_seq_captacao_w      		mprev_captacao.nr_sequencia%type;
	nr_seq_grupo_tema_w       	mprev_grupo_tema_encontro.nr_sequencia%type;
	nr_seq_mprev_atend_w      	w_mprev_agendamento.nr_seq_mprev_atend%type;
	nr_seq_partic_ciclo_item_w	w_mprev_agendamento.nr_seq_partic_ciclo_item%type;
	nr_seq_participante_w		w_mprev_agendamento.nr_seq_participante%type;
	nr_seq_status_pac_w    		w_mprev_agendamento.nr_seq_status_pac%type;
	nr_seq_turma_w      		w_mprev_agendamento.nr_seq_turma%type;
	nr_seq_w_agendamento_w		w_mprev_sel_partic_agenda.nr_sequencia%type;
	nr_seq_motivo_canc_w		w_mprev_agendamento.nr_seq_motivo_canc%type;
	nr_seq_forma_atend_w		mprev_forma_atendimento.nr_sequencia%type;

	
BEGIN

	select	max(b.nr_seq_w_agendamento)
	into STRICT	nr_seq_w_agendamento_w
	from	w_mprev_agendamento a,
		w_mprev_sel_partic_agenda b
	where	a.nr_sequencia = b.nr_seq_w_agendamento
	and	a.nr_seq_agendamento = nr_seq_agendamento_p;

	/*Deleta os registros na tabela W*/

	delete	from	w_mprev_sel_partic_agenda
	where	nr_seq_w_agendamento = nr_seq_w_agendamento_w;

	/*Deletar os registros da tabela w_mprev_ag_ativ_item*/

	delete	from w_mprev_ag_ativ_item a
	where	exists (SELECT 1
			from	w_mprev_agendamento x,
				w_mprev_agendamento_ativ y
			where	x.nr_sequencia = y.nr_seq_w_agendamento
			and	y.nr_sequencia = a.nr_seq_w_agendamento_ativ
			and	x.nr_seq_agendamento = nr_seq_agendamento_p);

	/*Deletar os registros da tabela w_mprev_agendamento_ativ*/

	delete	from w_mprev_agendamento_ativ a
	where	exists (SELECT	1
		from	w_mprev_agendamento x
		where	x.nr_sequencia	= a.nr_seq_w_agendamento
		and	x.nr_seq_agendamento = nr_seq_agendamento_p);
		
	/*Deletar os registros da tabela w_hdm_agendamento_anexo*/

	delete	from w_hdm_agendamento_anexo a
	where	exists (SELECT	1
					from	w_mprev_agendamento x
					where	x.nr_sequencia	= a.nr_seq_agendamento
					and		x.nr_seq_agendamento = nr_seq_agendamento_p);

	/*Limpar o agendamento na tabela w_mprev_agendamento*/

	delete	from	w_mprev_agendamento
	where	nr_seq_agendamento = nr_seq_agendamento_p;

	/*Alimentar os dados da tabela mprev_agendamento na tablema w_mprev_agendamento*/

	select	cd_agenda,
		cd_agenda_local,
		cd_agenda_profissional,
		coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento),
		ds_observacao,
		ds_utilizacao,
		dt_agenda,
		dt_atualizacao,
		dt_atualizacao_nrec,
		ie_forma_atendimento,
		ie_previsto,
		ie_tipo_agendamento,
		ie_tipo_atendimento,
		nm_usuario,
		nm_usuario_nrec,
		nr_minuto_duracao,
		nr_seq_ativ_extra,
		nr_seq_captacao,
		nr_seq_grupo_tema,
		nr_seq_mprev_atend,
		nr_seq_partic_ciclo_item,
		nr_seq_participante,
		nr_seq_status_pac,
		nr_seq_turma,
		nr_seq_motivo_canc,
		nr_seq_forma_atend
	into STRICT
		cd_agenda_w,
		cd_agenda_local_w,
		cd_agenda_profissional_w,
		cd_estabelecimento_w,
		ds_observacao_w,
		ds_utilizacao_w,
		dt_agenda_w,
		dt_atualizacao_w,
		dt_atualizacao_nrec_w,
		ie_forma_atendimento_w,
		ie_previsto_w,
		ie_tipo_agendamento_w,
		ie_tipo_atendimento_w,
		nm_usuario_w,
		nm_usuario_nrec_w,
		nr_minuto_duracao_w,
		nr_seq_ativ_extra_w,
		nr_seq_captacao_w,
		nr_seq_grupo_tema_w,
		nr_seq_mprev_atend_w,
		nr_seq_partic_ciclo_item_w,
		nr_seq_participante_w,
		nr_seq_status_pac_w,
		nr_seq_turma_w,
		nr_seq_motivo_canc_w,
		nr_seq_forma_atend_w
	from	mprev_agendamento
	where	nr_sequencia = nr_seq_agendamento_p;

	/*------Insere os registros na tabela w_mprev_agendamento------------*/

	insert into w_mprev_agendamento(nr_sequencia,
		cd_agenda,
		cd_agenda_local,
		cd_agenda_profissional,
		cd_estabelecimento,
		ds_observacao,
		ds_utilizacao,
		dt_agenda,
		dt_atualizacao,
		dt_atualizacao_nrec,
		ie_forma_atendimento,
		ie_previsto,
		ie_tipo_agendamento,
		ie_tipo_atendimento,
		nm_usuario,
		nm_usuario_nrec,
		nr_minuto_duracao,
		nr_seq_agendamento,
		nr_seq_ativ_extra,
		nr_seq_captacao,
		nr_seq_grupo_tema,
		nr_seq_mprev_atend,
		nr_seq_partic_ciclo_item,
		nr_seq_participante,
		nr_seq_status_pac,
		nr_seq_turma,
		nr_seq_motivo_canc,
		nr_seq_forma_atend)
	values (nextval('w_mprev_agendamento_seq'),
		cd_agenda_w,
		cd_agenda_local_w,
		cd_agenda_profissional_w,
		cd_estabelecimento_w,
		ds_observacao_w,
		ds_utilizacao_w,
		dt_agenda_w,
		dt_atualizacao_w,
		dt_atualizacao_nrec_w,
		ie_forma_atendimento_w,
		ie_previsto_w,
		ie_tipo_agendamento_w,
		ie_tipo_atendimento_w,
		nm_usuario_w,
		nm_usuario_nrec_w,
		nr_minuto_duracao_w,
		nr_seq_agendamento_p,
		nr_seq_ativ_extra_w,
		nr_seq_captacao_w,
		nr_seq_grupo_tema_w,
		nr_seq_mprev_atend_w,
		nr_seq_partic_ciclo_item_w,
		nr_seq_participante_w,
		nr_seq_status_pac_w,
		nr_seq_turma_w,
		nr_seq_motivo_canc_w,
		nr_seq_forma_atend_w);

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE mprev_agenda_pck.carregar_w_agendamento (nr_seq_agendamento_p bigint) FROM PUBLIC;
