SET client_encoding TO 'UTF8';


	/*---------------------------------------------------------------------------------------------------------------------------------------------
	| CARREGAR OS DADOS DA TABELA MPREV_AGENDAMENTO_RECURSO PARA W_MPREV_AGENDAMENTO_REC                |
	*/
CREATE OR REPLACE PROCEDURE mprev_agenda_pck.carregar_w_agendamento_recurso (nr_seq_agendamento_p bigint) AS $body$
DECLARE


	nr_seq_w_agendamento_w	w_mprev_agendamento.nr_sequencia%type;

	C01 CURSOR FOR
		SELECT	dt_atualizacao,
			dt_atualizacao_nrec,
			ie_tipo_recurso,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_agendamento,
			nr_seq_recurso
		from	mprev_agendamento_recurso
		where	nr_seq_agendamento = nr_seq_agendamento_p;

	
BEGIN

	/*Busca sequencia da tabela w_mprev_agendamento para inserir no campo nr_seq_w_agendamento*/

	select	max(nr_sequencia)
	into STRICT	nr_seq_w_agendamento_w
	from	w_mprev_agendamento
	where	nr_seq_agendamento = nr_seq_agendamento_p;

	/*Deleta os registros na tabela W*/

	delete	from w_mprev_agendamento_rec
	where	nr_seq_w_agendamento = nr_seq_w_agendamento_w;

	for r_C01 in C01 loop

		insert into w_mprev_agendamento_rec(nr_sequencia,
			dt_atualizacao,
			dt_atualizacao_nrec,
			ie_tipo_recurso,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_recurso,
			nr_seq_w_agendamento)
		values (nextval('w_mprev_agendamento_rec_seq'),
			r_C01.dt_atualizacao,
			r_C01.dt_atualizacao_nrec,
			r_C01.ie_tipo_recurso,
			r_C01.nm_usuario,
			r_C01.nm_usuario_nrec,
			r_C01.nr_seq_recurso,
			nr_seq_w_agendamento_w);

		commit;

	end loop;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
