CREATE OR REPLACE PROCEDURE mprev_agenda_pck.confirmar_agenda ( nr_seq_agendamento_p bigint, ie_opcao_p text, ds_dados_p text, nr_seq_forma_confirm_p bigint, nm_usuario_p text) AS $body$
BEGIN

	if (ie_opcao_p = 'C') then
		update	mprev_agendamento
		set	dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			ds_confirmacao = ds_dados_p,
			nr_seq_forma_confirm = nr_seq_forma_confirm_p,
			ie_status_agenda = 'CN',
			nm_usuario_confirm = nm_usuario_p,
			dt_confirmacao	= clock_timestamp()
		where	nr_sequencia = 	nr_seq_agendamento_p;
	elsif (ie_opcao_p = 'D') then
		update	mprev_agendamento
		set	dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			ds_confirmacao = ds_dados_p,
			nr_seq_forma_confirm = nr_seq_forma_confirm_p,
			ie_status_agenda = 'A',
			nm_usuario_confirm  = NULL,
			dt_confirmacao	 = NULL
		where	nr_sequencia = 	nr_seq_agendamento_p;
	end if;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
