	/*---------------------------------------------------------------------------------------------------------------------------------------------
	| GRAVAR OS DADOS DA TABELA W_HDM_AGENDAMENTO_ANEXO NA HDM_AGENDAMENTO_ANEXO	|
	*/
CREATE OR REPLACE PROCEDURE mprev_agenda_pck.gravar_hdm_agendamento_anexo (nr_seq_w_agendamento_p bigint, nr_seq_agendamento_p bigint) AS $body$
DECLARE


	C01 CURSOR FOR
		SELECT	a.ds_arquivo,
				a.ds_observacao,
				a.ds_titulo,
				a.dt_atualizacao,
				a.dt_atualizacao_nrec,
				a.nm_usuario,
				a.nm_usuario_nrec
		from	w_hdm_agendamento_anexo a,
				w_mprev_agendamento b
		where	b.nr_sequencia = a.nr_seq_agendamento
		and		a.nr_seq_agendamento = nr_seq_w_agendamento_p
		order by a.nr_sequencia;

	
BEGIN

	delete	from hdm_agendamento_anexo
	where	nr_seq_agendamento = nr_seq_agendamento_p;

	for r_C01 in C01 loop
		insert into hdm_agendamento_anexo(nr_sequencia,
			ds_arquivo,
			ds_observacao,
			ds_titulo,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_agendamento)
		values (nextval('hdm_agendamento_anexo_seq'),
			r_C01.ds_arquivo,
			r_C01.ds_observacao,
			r_C01.ds_titulo,
			r_C01.dt_atualizacao,
			r_C01.dt_atualizacao_nrec,
			r_C01.nm_usuario,
			r_C01.nm_usuario_nrec,
			nr_seq_agendamento_p);

		commit;

	end loop;

	delete	from w_hdm_agendamento_anexo a
	where	exists (SELECT	1
					from	w_mprev_agendamento x
					where	x.nr_sequencia	= a.nr_seq_agendamento
					and	x.nr_seq_agendamento = nr_seq_agendamento_p);

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
