	/*---------------------------------------------------------------------------------------------------------------------------------------------
	| GRAVAR OS DADOS DA TABELA w_mprev_agendamento_rec PARA mprev_agendamento_recurso                 |
	*/
CREATE OR REPLACE PROCEDURE mprev_agenda_pck.gravar_mprev_agendamento_rec (nr_seq_w_agendamento_p bigint, nr_seq_agendamento_p bigint) AS $body$
DECLARE


	C01 CURSOR FOR
		SELECT	a.nr_sequencia,
			a.dt_atualizacao,
			a.nm_usuario,
			a.dt_atualizacao_nrec,
			a.nm_usuario_nrec,
			a.nr_seq_w_agendamento,
			a.ie_tipo_recurso,
			a.nr_seq_recurso
		from	w_mprev_agendamento_rec a,
			w_mprev_agendamento b
		where	b.nr_sequencia = a.nr_seq_w_agendamento
		and		a.nr_seq_w_agendamento = nr_seq_w_agendamento_p
		order by a.nr_sequencia;

	
BEGIN

	delete	from	mprev_agendamento_recurso
	where	nr_seq_agendamento = nr_seq_agendamento_p;

	for r_C01 in C01 loop
		insert into mprev_agendamento_recurso(nr_sequencia,
			 dt_atualizacao,
			 nm_usuario,
			 dt_atualizacao_nrec,
			 nm_usuario_nrec,
			 nr_seq_agendamento,
			 ie_tipo_recurso,
			 nr_seq_recurso)
		values (nextval('mprev_agendamento_recurso_seq'),
			r_C01.dt_atualizacao,
			r_C01.nm_usuario,
			r_C01.dt_atualizacao_nrec,
			r_C01.nm_usuario_nrec,
			nr_seq_agendamento_p,
			r_C01.ie_tipo_recurso,
			r_C01.nr_seq_recurso);

		commit;

	end loop;

	delete	from w_mprev_agendamento_rec a
	where	exists (SELECT	1
			from	w_mprev_agendamento x
			where	x.nr_sequencia	= a.nr_seq_w_agendamento
			and	x.nr_seq_agendamento = nr_seq_agendamento_p);

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
