	/*---------------------------------------------------------------------------------------------------------------------------------------------
	| 		TRANSFERIR AS ANEXOS DO AGENDAMENTO TRANSFERIDO		                 |
	*/
CREATE OR REPLACE PROCEDURE mprev_agenda_pck.transferir_anexos_agenda (nr_seq_agendamento_ant_p bigint, nr_seq_agendamento_nov_p bigint, nm_usuario_p text) AS $body$
DECLARE


	C01 CURSOR FOR
		SELECT	ds_arquivo,
				ds_observacao,
				ds_titulo,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,	
				nm_usuario_nrec
		from	hdm_agendamento_anexo
		where	nr_seq_agendamento = nr_seq_agendamento_ant_p
		order by nr_sequencia;

	
BEGIN
		
		for r_C01 in C01 loop

		insert into hdm_agendamento_anexo(nr_sequencia,
			ds_arquivo,
			ds_observacao,
			ds_titulo,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_agendamento)
		values (nextval('hdm_agendamento_anexo_seq'),
			r_C01.ds_arquivo,
			r_C01.ds_observacao,
			r_C01.ds_titulo,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			nr_seq_agendamento_nov_p);

		end loop;
	
		commit;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
