	/*---------------------------------------------------------------------------------------------------------------------------------------------
	| 		TRANSFERIR AS ATIVIDADES DO AGENDAMENTO TRANSFERIDO		                 |
	*/
CREATE OR REPLACE PROCEDURE mprev_agenda_pck.transferir_atividades_agenda (nr_seq_agendamento_ant_p bigint, nr_seq_agendamento_nov_p bigint, nm_usuario_p text) AS $body$
DECLARE


	C01 CURSOR FOR
		SELECT	dt_atualizacao,
			ie_executado,
			nm_usuario,
			nr_seq_agendamento,
			nr_seq_atividade,
			nr_seq_ativ_plano_aval,
			nr_seq_ativ_plano_exame,
			nr_seq_part_cic_item_ativ
		from	mprev_agendamento_ativ
		where	nr_seq_agendamento = nr_seq_agendamento_ant_p
		order by nr_sequencia;

	
BEGIN
		for r_C01 in C01 loop

		insert into mprev_agendamento_ativ(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_agendamento,
			nr_seq_atividade,
			ie_executado,
			nr_seq_ativ_plano_aval,
			nr_seq_ativ_plano_exame,
			nr_seq_part_cic_item_ativ)
		values (nextval('mprev_agendamento_ativ_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_agendamento_nov_p,
			r_C01.nr_seq_atividade,
			r_C01.ie_executado,
			r_C01.nr_seq_ativ_plano_aval,
			r_C01.nr_seq_ativ_plano_exame,
			r_C01.nr_seq_part_cic_item_ativ);
					
		end loop;

	commit;
	
	END;

$body$
