	/*---------------------------------------------------------------------------------------------------------------------------------------------
	| TRANSFERIR OS PARTICIPANTES PREVISTOS TURMA DO AGENDAMENTO TRANSFERIDO		                 |
	*/
CREATE OR REPLACE PROCEDURE mprev_agenda_pck.transferir_previstos_turma_ag (nr_seq_agendamento_ant_p bigint, nr_seq_agendamento_nov_p bigint, nm_usuario_p text) AS $body$
DECLARE


	C01 CURSOR FOR
		SELECT	ds_justificativa_falta,
			dt_atualizacao,
			dt_atualizacao_nrec,
			ie_origem,
			ie_selecionado,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_agendamento,
			nr_seq_participante,
			ie_confirmado,
			nr_seq_motivo_falta
		from	mprev_sel_partic_agenda
		where	nr_seq_agendamento = nr_seq_agendamento_ant_p
		order by nr_sequencia;

	
BEGIN

		for r_C01 in C01 loop

			insert into mprev_sel_partic_agenda(nr_sequencia,
			ds_justificativa_falta,
			dt_atualizacao,
			dt_atualizacao_nrec,
			ie_origem,
			ie_selecionado,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_agendamento,
			nr_seq_participante,
			ie_confirmado,
			nr_seq_motivo_falta)
		values (nextval('mprev_sel_partic_agenda_seq'),
			r_C01.ds_justificativa_falta,
			r_C01.dt_atualizacao,
			clock_timestamp(),
			r_C01.ie_origem,
			r_C01.ie_selecionado,
			r_C01.nm_usuario,
			nm_usuario_p,
			nr_seq_agendamento_nov_p,
			r_C01.nr_seq_participante,
			r_C01.ie_confirmado,
			r_C01.nr_seq_motivo_falta);

		commit;

		end loop;

	END;

$body$
