	/*---------------------------------------------------------------------------------------------------------------------------------------------
	| TRANSFERIR OS RECURSOS DO AGENDAMENTO TRANSFERIDO		                 |
	*/
CREATE OR REPLACE PROCEDURE mprev_agenda_pck.transferir_recursos_agenda (nr_seq_agendamento_ant_p bigint, nr_seq_agendamento_nov_p bigint, nm_usuario_p text) AS $body$
DECLARE


	C01 CURSOR FOR
		SELECT	dt_atualizacao,
			dt_atualizacao_nrec,
			ie_tipo_recurso,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_agendamento,
			nr_seq_recurso,
			nr_sequencia
		from	mprev_agendamento_recurso
		where	nr_seq_agendamento = nr_seq_agendamento_ant_p
		order by nr_sequencia;

	
BEGIN
		for r_C01 in C01 loop

			insert into mprev_agendamento_recurso(nr_sequencia,
			dt_atualizacao,
			dt_atualizacao_nrec,
			ie_tipo_recurso,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_agendamento,
			nr_seq_recurso)
		values (nextval('mprev_agendamento_recurso_seq'),
			r_C01.dt_atualizacao,
			clock_timestamp(),
			r_C01.ie_tipo_recurso,
			r_C01.nm_usuario,
			nm_usuario_p,
			nr_seq_agendamento_nov_p,
			r_C01.nr_seq_recurso);

		commit;

		end loop;

	END;

$body$
