CREATE OR REPLACE PROCEDURE mprev_base_cubo_prest_pck.atualizar_base ( dt_referencia_p timestamp, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


dt_inicio_w		timestamp;
dt_fim_w		timestamp;
nr_sequencia_w	mprev_regra_dados_cubo_ops.nr_sequencia%type;
qt_meses_atras_w	mprev_regra_dados_cubo_ops.qt_meses_atras%type;


BEGIN

select	a.nr_sequencia,
		coalesce(a.qt_meses_atras, 12) qt_meses_atras
into STRICT	nr_sequencia_w,
		qt_meses_atras_w
from	mprev_regra_dados_cubo_ops a
where	a.cd_estabelecimento	= cd_estabelecimento_p;

CALL CALL mprev_base_cubo_prest_pck.limpar_base();

dt_inicio_w	:= pkg_date_utils.add_month(pkg_date_utils.start_of(coalesce(dt_referencia_p, clock_timestamp()),'MONTH', 0),-qt_meses_atras_w, 0);
dt_fim_w	:= fim_dia(pkg_date_utils.end_of(coalesce(dt_referencia_p, clock_timestamp()), 'MONTH', 0));

-- Atualizar a base de pessoas e beneficiarios.
CALL CALL mprev_base_cubo_prest_pck.atualizar_pessoas(dt_inicio_w, dt_fim_w, nm_usuario_p);
	
END;

$body$
