CREATE OR REPLACE PROCEDURE mprev_pop_alvo_pck.inserir_data ( nr_seq_populacao_alvo_p bigint, dt_referencia_p timestamp, nm_usuario_p text, nr_seq_pop_alvo_data_p INOUT bigint) AS $body$
DECLARE


	nr_seq_retorno_w	mprev_pop_alvo_data.nr_sequencia%type;

	
BEGIN

	begin
	select	a.nr_sequencia
	into STRICT	nr_seq_retorno_w
	from	mprev_pop_alvo_data a
	where	a.nr_seq_populacao_alvo = nr_seq_populacao_alvo_p
	and	a.nr_mes	= (PKG_DATE_UTILS.extract_field('MONTH',dt_referencia_p))::numeric
	and	a.nr_ano	= (PKG_DATE_UTILS.extract_field('YEAR',dt_referencia_p))::numeric;
	exception
		when others then
		nr_seq_retorno_w	:= null;
	end;

	/* Se nao achou data ja na populacao, insere */


	if (coalesce(nr_seq_retorno_w::text, '') = '') then

		insert into mprev_pop_alvo_data(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nr_seq_populacao_alvo,
			nr_mes,
			nr_trimestre,
			nr_semestre,
			nr_ano,
			dt_mes)
		values (nextval('mprev_pop_alvo_data_seq'),
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_populacao_alvo_p,
			(PKG_DATE_UTILS.extract_field('MONTH',dt_referencia_p))::numeric ,
			(to_char(dt_referencia_p,'q'))::numeric ,
			round(to_char(dt_referencia_p,'q')/2),
			(PKG_DATE_UTILS.extract_field('YEAR',dt_referencia_p))::numeric ,
			pkg_date_utils.start_of(dt_referencia_p,'MONTH', 0))
		returning nr_sequencia into nr_seq_retorno_w;
	end if;

	nr_seq_pop_alvo_data_p	:= nr_seq_retorno_w;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
