			' 		and	x.ie_selecionar	= '|| current_setting('mprev_pop_alvo_prest_pck.aspas_w')::pls_util_pck.aspas_w%type || ie_exists_p || current_setting('mprev_pop_alvo_prest_pck.aspas_w')::pls_util_pck.aspas_w%type ||') ';

	
