SET client_encoding TO 'UTF8';


	-->>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>INSERIR COMPLEMENTO PESSOAS<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--
CREATE OR REPLACE PROCEDURE mprev_pop_alvo_prest_pck.selecionar_compl_pf ( nr_seq_regra_cubo_p bigint, nr_seq_populacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


	c_regra_compl CURSOR(nr_seq_regra_cubo_pc	bigint) FOR
		SELECT	a.cd_municipio_ibge,
            a.sg_estado,
		        a.ie_tipo_complemento,
			      a.ie_incluir
		from	hdm_regra_cubo_compl a
		where	a.nr_seq_regra_cubo	= nr_seq_regra_cubo_pc;

	tabela_regra_compl_pf_w		table_regra_compl_pf;
	nr_seq_benef_w			bigint;
	cursor_w			sql_pck.t_cursor;

	-- Tabela para inserir a sequencia das que retorna do select dinamico "ds_select_w".
	tb_pop_alvo_sel_compl_pf_w	pls_util_cta_pck.t_number_table;	

	
BEGIN

	update	mprev_populacao_alvo
	set	dt_inicio_filtro_pessoa	= clock_timestamp()
	where	nr_sequencia	= nr_seq_populacao_p;

	/* Montar select dinamico e cria pessoas conforme regra de Complemento (Endereco) */

	PERFORM set_config('mprev_pop_alvo_prest_pck.ds_select_w', ' select	pessoa.nr_sequencia ' || current_setting('mprev_pop_alvo_prest_pck.enter_w')::pls_util_pck.enter_w%type ||
				' from		mprev_pessoa_cubo_ops pessoa, ' || current_setting('mprev_pop_alvo_prest_pck.enter_w')::pls_util_pck.enter_w%type ||
				'		compl_pessoa_fisica compl ' || current_setting('mprev_pop_alvo_prest_pck.enter_w')::pls_util_pck.enter_w%type ||
				' where 	pessoa.cd_pessoa_fisica = compl.cd_pessoa_fisica ', false);

	if (nr_seq_regra_cubo_p IS NOT NULL AND nr_seq_regra_cubo_p::text <> '')then
	
		for r_c_regra_compl in c_regra_compl(nr_seq_regra_cubo_p) loop
		
			tabela_regra_compl_pf_w.cd_municipio_ibge	:= r_c_regra_compl.cd_municipio_ibge;
      tabela_regra_compl_pf_w.sg_estado	:= r_c_regra_compl.sg_estado;
			tabela_regra_compl_pf_w.ie_tipo_complemento     := r_c_regra_compl.ie_tipo_complemento;
			tabela_regra_compl_pf_w.ie_incluir		:= r_c_regra_compl.ie_incluir;

			PERFORM set_config('mprev_pop_alvo_prest_pck.ds_restricao_w', mprev_pop_alvo_prest_pck.obter_restricao_compl_pf(tabela_regra_compl_pf_w), false);

			PERFORM set_config('mprev_pop_alvo_prest_pck.ds_select_comp_w', current_setting('mprev_pop_alvo_prest_pck.ds_select_w')::varchar(4000) || current_setting('mprev_pop_alvo_prest_pck.ds_restricao_w')::varchar(4000) || ' group by pessoa.nr_sequencia ', false);

			current_setting('mprev_pop_alvo_prest_pck.bind_sql_w')::sql_pck.t_dado_bind := sql_pck.executa_sql_cursor(current_setting('mprev_pop_alvo_prest_pck.ds_select_comp_w')::varchar(4000), current_setting('mprev_pop_alvo_prest_pck.bind_sql_w')::sql_pck.t_dado_bind);

			loop

				tb_pop_alvo_sel_compl_pf_w.delete;

				fetch	cursor_w bulk collect
				into	tb_pop_alvo_sel_compl_pf_w
				limit	current_setting('mprev_pop_alvo_prest_pck.qt_reg_commit_w')::integer;

				exit when tb_pop_alvo_sel_compl_pf_w.count = 0;

				-- Incluir as pessoas na selecao
				forall i in tb_pop_alvo_sel_compl_pf_w.first .. tb_pop_alvo_sel_compl_pf_w.last
					insert into mprev_pop_alvo_selecao(nr_sequencia, nm_usuario, dt_atualizacao,
						nr_seq_populacao_alvo, nr_seq_pessoa_cubo, ie_selecionar,
						nm_tabela_regra_cubo, nr_seq_tabela_regra_cubo)
					values (nextval('mprev_pop_alvo_selecao_seq'), nm_usuario_p, clock_timestamp(),
						nr_seq_populacao_p, tb_pop_alvo_sel_compl_pf_w(i),
						tabela_regra_compl_pf_w.ie_incluir, 'HDM_REGRA_CUBO_COMPL', 7);
				commit;
			end loop;
			close cursor_w;
		end loop;
		commit;

	end if;

	update	mprev_populacao_alvo
	set	dt_fim_filtro_pessoa	= clock_timestamp()
	where	nr_sequencia	= nr_seq_populacao_p;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
