	-->>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>INSERIR LISTA PROBLEMA<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--
CREATE OR REPLACE PROCEDURE mprev_pop_alvo_prest_pck.selecionar_lista_prob ( nr_seq_regra_cubo_p bigint, nr_seq_populacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


	c_regra_lista_prob CURSOR(nr_seq_regra_cubo_pc	bigint) FOR
		SELECT	a.nr_sequencia,
		        a.cd_ciap,
				cd_doenca,
				cd_doenca_inicial,
				cd_doenca_final,
				ie_status,
				qt_ocorrencia,
				ie_incluir
		from	hdm_regra_cubo_lista_prob a
		where	a.nr_seq_regra_cubo	= nr_seq_regra_cubo_pc;
		
	regra_lista_prob_w		regra_lista_prob;
	cursor_w		sql_pck.t_cursor;

	-- Tabela para inserir a sequencia do beneficiario que retorna do select dinamico "ds_select_w".
	tb_pop_alvo_sel_lista_prob_w	pls_util_cta_pck.t_number_table;

	
BEGIN

	
	if (nr_seq_regra_cubo_p IS NOT NULL AND nr_seq_regra_cubo_p::text <> '')then
	
	/* Monta select dinamico*/

		PERFORM set_config('mprev_pop_alvo_prest_pck.ds_select_w', ' select	pessoa.nr_sequencia 				' || current_setting('mprev_pop_alvo_prest_pck.enter_w')::pls_util_pck.enter_w%type ||
						' from		mprev_pessoa_cubo_ops pessoa, 			' || current_setting('mprev_pop_alvo_prest_pck.enter_w')::pls_util_pck.enter_w%type ||
						' 			lista_problema_pac lista			' || current_setting('mprev_pop_alvo_prest_pck.enter_w')::pls_util_pck.enter_w%type ||
						' where		pessoa.cd_pessoa_fisica = lista.cd_pessoa_fisica ', false);
						
	
		for r_c_regra_lista_prob in c_regra_lista_prob(nr_seq_regra_cubo_p) loop
			PERFORM set_config('mprev_pop_alvo_prest_pck.ds_restricao_w', null, false);
			regra_lista_prob_w.nr_sequencia	:= r_c_regra_lista_prob.nr_sequencia;
			
			regra_lista_prob_w.cd_ciap	:= r_c_regra_lista_prob.cd_ciap;
			regra_lista_prob_w.cd_doenca	:= r_c_regra_lista_prob.cd_doenca;
			regra_lista_prob_w.cd_doenca_inicial	:= r_c_regra_lista_prob.cd_doenca_inicial;
			regra_lista_prob_w.cd_doenca_final	:= r_c_regra_lista_prob.cd_doenca_final;
			regra_lista_prob_w.ie_status	:= r_c_regra_lista_prob.ie_status;
			regra_lista_prob_w.qt_ocorrencia	:= r_c_regra_lista_prob.qt_ocorrencia;
			regra_lista_prob_w.ie_incluir		:= r_c_regra_lista_prob.ie_incluir;

			PERFORM set_config('mprev_pop_alvo_prest_pck.ds_restricao_w', mprev_pop_alvo_prest_pck.obter_restricao_lista_prob(regra_lista_prob_w) ||' --'|| regra_lista_prob_w.nr_sequencia, false);

			PERFORM set_config('mprev_pop_alvo_prest_pck.ds_select_comp_w', current_setting('mprev_pop_alvo_prest_pck.ds_select_w')::varchar(4000) ||current_setting('mprev_pop_alvo_prest_pck.enter_w')::pls_util_pck.enter_w%type|| current_setting('mprev_pop_alvo_prest_pck.ds_restricao_w')::varchar(4000), false);			
			
						
			current_setting('mprev_pop_alvo_prest_pck.bind_sql_w')::sql_pck.t_dado_bind := sql_pck.executa_sql_cursor(current_setting('mprev_pop_alvo_prest_pck.ds_select_comp_w')::varchar(4000), current_setting('mprev_pop_alvo_prest_pck.bind_sql_w')::sql_pck.t_dado_bind);
			
			loop

				tb_pop_alvo_sel_lista_prob_w.delete;

				fetch	cursor_w bulk collect
				into	tb_pop_alvo_sel_lista_prob_w
				limit	current_setting('mprev_pop_alvo_prest_pck.qt_reg_commit_w')::integer;

				exit when tb_pop_alvo_sel_lista_prob_w.count = 0;

				-- Incluir as pessoas na selecao
				forall i in tb_pop_alvo_sel_lista_prob_w.first .. tb_pop_alvo_sel_lista_prob_w.last
					insert into mprev_pop_alvo_selecao(nr_sequencia, nm_usuario, dt_atualizacao,
						nr_seq_populacao_alvo, nr_seq_pessoa_cubo, ie_selecionar,
						nm_tabela_regra_cubo, nr_seq_tabela_regra_cubo)
					values (nextval('mprev_pop_alvo_selecao_seq'), nm_usuario_p, clock_timestamp(),
						nr_seq_populacao_p, tb_pop_alvo_sel_lista_prob_w(i),
						regra_lista_prob_w.ie_incluir, 'HDM_REGRA_CUBO_LISTA_PROB', 3);
				commit;
			end loop;
			close cursor_w;
		end loop;
		commit;
	end if;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
