CREATE OR REPLACE FUNCTION new_icu_notifications_pck.get_approval_items (nm_usuario_p text, cd_pessoa_fisica_p text, cd_perfil_p bigint, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_needs_approval_w  integer;


BEGIN

	select	sum(qt_pendente)
	into STRICT	qt_needs_approval_w
	from
	(SELECT	count(*) qt_pendente
	from    cpoe_material
	where   nr_atendimento = nr_atendimento_p
	and     (dt_liberacao_aux IS NOT NULL AND dt_liberacao_aux::text <> '')
	and     coalesce(dt_liberacao::text, '') = ''
	and     ((CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  coalesce(dt_fim_cih, dt_fim)  ELSE coalesce(dt_suspensao, dt_fim) END  >= clock_timestamp())
	or (CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  coalesce(dt_fim_cih, dt_fim)  ELSE coalesce(dt_suspensao, dt_fim) coalesce(END::text, '') = ''))
	and     ((cd_medico = cd_pessoa_fisica_p) or (cpoe_verifica_regra_aprovacao(cd_perfil_p, nm_usuario_p, 'AX') = 'S'))
	
union all

	SELECT	count(*) qt_pendente
