CREATE OR REPLACE FUNCTION new_icu_notifications_pck.get_new_orders_message (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


  qt_pending_ack_w  bigint := 0;
  ds_message_w  varchar(100);


BEGIN
  if (nr_atendimento_p > 0) then
    qt_pending_ack_w:= new_icu_notifications_pck.get_new_orders_items(nr_atendimento_p);

    if (qt_pending_ack_w > 0) then
      ds_message_w:= wheb_mensagem_pck.get_texto(1119570,'DS_TOTAL_P='||'('||qt_pending_ack_w||')');
    end if;
  end if;

  return ds_message_w;

  END;

$body$
