SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION new_icu_notifications_pck.get_pending_cosign_adm (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_pending_cosign_w  bigint := 0;


BEGIN

	select  count(*)
    into STRICT  	qt_pending_cosign_w
    from   	prescr_mat_hor
    where   nr_prescricao in (SELECT b.nr_prescricao from prescr_medica b where b.nr_atendimento = nr_atendimento_p)
    and   	obter_status_prim_check(dt_primeira_checagem, dt_fim_horario, dt_suspensao, dt_recusa) = 'S'
    and    	coalesce(dt_suspensao::text, '') = ''
    and   	coalesce(dt_fim_horario::text, '') = ''
    and   	(dt_lib_horario IS NOT NULL AND dt_lib_horario::text <> '');

	return qt_pending_cosign_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
