CREATE OR REPLACE FUNCTION new_icu_notifications_pck.get_pending_verif_items ( cd_pessoa_fisica_p text, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


  qt_pending_ver_w  bigint := 0;


BEGIN

    select  sum(qt_pendente)
    into STRICT  qt_pending_ver_w
    from (
        SELECT  count(*) qt_pendente
        from    cpoe_material
        where  nr_atendimento = nr_atendimento_p
        and   (dt_liberacao_aux IS NOT NULL AND dt_liberacao_aux::text <> '')
        and    coalesce(dt_liberacao::text, '') = ''
        and    cd_medico = cd_pessoa_fisica_p
        
union all

        SELECT  count(*) qt_pendente
        from    cpoe_dieta
        where  nr_atendimento = nr_atendimento_p
        and   (dt_liberacao_aux IS NOT NULL AND dt_liberacao_aux::text <> '')
        and    coalesce(dt_liberacao::text, '') = ''
        and    cd_medico = cd_pessoa_fisica_p
        
union all

        select  count(*) qt_pendente
        from  cpoe_recomendacao
        where  nr_atendimento = nr_atendimento_p
        and   (dt_liberacao_aux IS NOT NULL AND dt_liberacao_aux::text <> '')
        and    coalesce(dt_liberacao::text, '') = ''
        and    cd_medico = cd_pessoa_fisica_p
        
union all

        select  count(*) qt_pendente
        from    cpoe_gasoterapia
        where  nr_atendimento = nr_atendimento_p
        and   (dt_liberacao_aux IS NOT NULL AND dt_liberacao_aux::text <> '')
        and    coalesce(dt_liberacao::text, '') = ''
        and    cd_medico = cd_pessoa_fisica_p
        
union all

        select  count(*) qt_pendente
        from    cpoe_procedimento
        where  nr_atendimento = nr_atendimento_p
        and   (dt_liberacao_aux IS NOT NULL AND dt_liberacao_aux::text <> '')
        and    coalesce(dt_liberacao::text, '') = ''
        and    cd_medico = cd_pessoa_fisica_p
        
union all

        select  count(*) qt_pendente
        from    cpoe_dialise
        where  nr_atendimento = nr_atendimento_p
        and   (dt_liberacao_aux IS NOT NULL AND dt_liberacao_aux::text <> '')
        and    coalesce(dt_liberacao::text, '') = ''
        and    cd_medico = cd_pessoa_fisica_p) alias19;

    return qt_pending_ver_w;

  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
