SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION new_icu_notifications_pck.get_sub_cosign_items (nr_atendimento_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


cd_pessoa_fisica_w 	pessoa_fisica.cd_pessoa_fisica%type;
cd_perfil_w			perfil.cd_perfil%type;
qt_cosign_items_w	bigint;


BEGIN

	cd_pessoa_fisica_w := obter_codigo_usuario(nm_usuario_p);
	cd_perfil_w	:= obter_perfil_ativo;

	select  count(*)
	into STRICT	qt_cosign_items_w
	from   	cpoe_material
	where   nr_atendimento = nr_atendimento_p
	and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and     coalesce(dt_ciencia_medico::text, '') = ''
	and     coalesce(ie_prescritor_aux, 'N') = 'S'
	and     coalesce(ie_intervencao_farm, 'N') = 'N'
	and     ie_exige_ciencia = 'V'
	and     ((CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  coalesce(dt_fim_cih, dt_fim)  ELSE coalesce(dt_suspensao, dt_fim) END  >= clock_timestamp())
	or (CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  coalesce(dt_fim_cih, dt_fim)  ELSE coalesce(dt_suspensao, dt_fim) coalesce(END::text, '') = ''))
	and     (cd_medico IS NOT NULL AND cd_medico::text <> '')
	and     cd_medico <> cd_pessoa_fisica_w
	and    	cpoe_verifica_regra_aprovacao(cd_perfil_w, nm_usuario_p, 'CA') = 'S';

	return qt_cosign_items_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
