SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION new_icu_notifications_pck.get_visualiza_alertas (nm_usuario_p text, ie_tipo_alerta_p text) RETURNS varchar AS $body$
DECLARE


ie_visualiza_alerta_w	varchar(1) := 'N';
cd_perfil_ativo_w		perfil.cd_perfil%type;
cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;


BEGIN

	cd_perfil_ativo_w := obter_perfil_ativo();
	cd_pessoa_fisica_w := obter_codigo_usuario(nm_usuario_p);

	select  CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_visualiza_alerta_w
	from    regra_not_integracao
	where   cd_registro_notificacao = ie_tipo_alerta_p
	and (cd_pessoa_fisica = cd_pessoa_fisica_w
	or      cd_perfil = cd_perfil_ativo_w);

	return ie_visualiza_alerta_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
