CREATE OR REPLACE FUNCTION new_icu_notifications_pck.integration_not_get_date (nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE

    string_retorno_w timestamp;

BEGIN

    select dt_atualizacao into STRICT string_retorno_w from (
    SELECT
    g.dt_atualizacao
    from integration_notifications g
    where g.nr_encounter = nr_atendimento_p
    and g.ie_type_record = 'SV'
    and g.ds_mensagem = 'SVI'
    and g.ie_viewed = 'N'
    and g.dt_atualizacao >= (clock_timestamp() - interval '1 days')
    order by g.dt_atualizacao desc) alias2 LIMIT 1;

    return string_retorno_w;

  END;

