CREATE OR REPLACE FUNCTION nicu_patient_demographics_pck.get_birth_weight (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_patient_w    nicu_encounter.nr_seq_patient%type;
qt_birth_weight_w   nicu_patient.qt_birth_weight%type;
ds_retorno_w	varchar(5) := '';


BEGIN
        select  max(enc.nr_seq_patient),
                max(np.qt_birth_weight)
        into STRICT    nr_seq_patient_w,
                qt_birth_weight_w
	from    nicu_encounter enc,
                nicu_patient np
	where   enc.nr_sequencia = nr_atendimento_p
        and     np.nr_sequencia = enc.nr_seq_patient;

        if (coalesce(qt_birth_weight_w, 0) = 0) then
		ds_retorno_w := nicu_patient_demographics_pck.get_last_obs_info(nr_seq_patient_w, 'DEMOG_Birth_Weight');
        end if;

        return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
