				and	x.cd_material_estoque	= cd_material_estoque_ww
				and	m.nr_prescricao		= b.nr_prescricao
				and	m.nr_prescricao		= a.nr_prescricao
				and	b.cd_setor_atendimento	= c.cd_setor_atendimento
				and	coalesce(a.cd_local_estoque, c.cd_local_estoque) = cd_local_estoque_p
				and	a.ie_status_cirurgia	in ('CB','AD')
				and	coalesce(a.ie_baixa_estoque_cir::text, '') = '';
			end if;
		end if;
	else
		if	((nr_seq_lote_p > 0) and (current_setting('obter_saldo_disp_estoque_pck.ie_estoque_lote_w')::material_estab.ie_estoque_lote%type = 'S')) then
			if (current_setting('obter_saldo_disp_estoque_pck.ie_disp_cirurgia_w')::parametro_estoque.ie_disp_cirurgia%type = 'S') then
				/*Busca as quantidades que estão em cirurgia no local do setor da cirurgia*/

				select	coalesce(sum(qt_total_dispensar),0)
				into STRICT	qt_cirurgia_w
				from	setor_atendimento c,
					cirurgia b,
					material x,
					prescr_material a,
					prescr_medica m
				where	a.cd_motivo_baixa		= 0
				and	a.cd_material		= x.cd_material
				and	x.cd_material_estoque	= cd_material_estoque_ww
				and	m.nr_prescricao		= b.nr_prescricao
				and	m.nr_prescricao		= a.nr_prescricao
				and	b.cd_setor_atendimento	= c.cd_setor_atendimento
				and	exists (	SELECT	1
						from	local_estoque loc
						where	loc.cd_local_estoque = c.cd_local_estoque
						and	loc.cd_estabelecimento = cd_estabelecimento_p
						and	loc.ie_tipo_local = '2'
						
union all

						SELECT	1
						from	local_estoque loc
						where	loc.cd_local_estoque = c.cd_local_estoque
						and	loc.cd_estabelecimento = cd_estabelecimento_p
						and	loc.ie_baixa_disp = 'S')
				and	a.ie_status_cirurgia	in ('CB','AD')
				and	coalesce(a.ie_baixa_estoque_cir::text, '') = ''
				and	a.nr_seq_lote_fornec 	= nr_seq_lote_p;
			elsif (current_setting('obter_saldo_disp_estoque_pck.ie_disp_cirurgia_w')::parametro_estoque.ie_disp_cirurgia%type = 'C') then
				/*Busca as quantidades que estão em cirurgia no local de consistência, se não ter local de consistência, busca do local do setor da cirurgia*/

				select	coalesce(sum(qt_total_dispensar),0)
