CREATE OR REPLACE FUNCTION obter_saldo_disp_estoque_pck.get_qt_quimioterapia ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_local_estoque_p bigint default null, nr_seq_lote_p bigint default null) RETURNS bigint AS $body$
DECLARE


	qt_quimioterapia_w		double precision	:= 0;
	cd_material_estoque_ww		material.cd_material%type;

	
BEGIN
	CALL CALL CALL CALL CALL CALL CALL CALL CALL obter_saldo_disp_estoque_pck.set_cd_estabelecimento(cd_estabelecimento_p);
	CALL obter_saldo_disp_estoque_pck.set_nr_seq_lote(nr_seq_lote_p);
	CALL obter_saldo_disp_estoque_pck.set_cd_material(cd_material_p);
	cd_material_estoque_ww		:=	obter_saldo_disp_estoque_pck.get_cd_material_estoque();

	if (current_setting('obter_saldo_disp_estoque_pck.ie_disp_quimioterapia_w')::parametro_estoque.ie_disp_quimioterapia%type = 'S') then
		if (cd_local_estoque_p > 0) then
			if	((nr_seq_lote_p > 0) and (current_setting('obter_saldo_disp_estoque_pck.ie_estoque_lote_w')::material_estab.ie_estoque_lote%type = 'S')) then
				select	coalesce(sum(a.qt_dose_real),0)
				into STRICT	qt_quimioterapia_w
				from	far_etapa_producao c,
					can_ordem_prod b,
					can_ordem_prod_mat a
				where	a.cd_material in (
						SELECT	x.cd_material
						from	material x
						where	x.cd_material_estoque = cd_material_estoque_ww)
				and	b.nr_sequencia = a.nr_seq_ordem
				and	coalesce(b.dt_fim_preparo::text, '') = ''
				and	coalesce(b.dt_entrega_setor::text, '') = ''
				and	coalesce(b.dt_devolucao::text, '') = ''
				and	b.ie_cancelada <> 'S'
				and	c.nr_sequencia = b.nr_seq_etapa_prod
				and	a.nr_seq_lote_fornec = nr_seq_lote_p
				and	c.nr_seq_cabine in (
						SELECT	x.nr_sequencia
						from	far_cabine_seg_biol x
						where	x.cd_local_estoque = current_setting('obter_saldo_disp_estoque_pck.cd_local_estoque_w')::local_estoque.cd_local_estoque%type);
			else
				select	coalesce(sum(a.qt_dose_real),0)
				into STRICT	qt_quimioterapia_w
				from	far_etapa_producao c,
					can_ordem_prod b,
					can_ordem_prod_mat a
				where	a.cd_material in (
						SELECT	x.cd_material
						from	material x
						where	x.cd_material_estoque = cd_material_estoque_ww)
				and	b.nr_sequencia = a.nr_seq_ordem
				and	coalesce(b.dt_fim_preparo::text, '') = ''
				and	coalesce(b.dt_entrega_setor::text, '') = ''
				and	coalesce(b.dt_devolucao::text, '') = ''
				and	b.ie_cancelada <> 'S'
				and	c.nr_sequencia = b.nr_seq_etapa_prod
				and	c.nr_seq_cabine in (
						SELECT	x.nr_sequencia
						from	far_cabine_seg_biol x
						where	x.cd_local_estoque = current_setting('obter_saldo_disp_estoque_pck.cd_local_estoque_w')::local_estoque.cd_local_estoque%type);
			end if;
		else
			if	((nr_seq_lote_p > 0) and (current_setting('obter_saldo_disp_estoque_pck.ie_estoque_lote_w')::material_estab.ie_estoque_lote%type = 'S')) then
				select	coalesce(sum(a.qt_dose_real),0)
				into STRICT	qt_quimioterapia_w
				from	far_etapa_producao c,
					can_ordem_prod b,
					can_ordem_prod_mat a
				where	a.cd_material in (
						SELECT	x.cd_material
						from	material x
						where	x.cd_material_estoque = cd_material_estoque_ww)
				and	b.nr_sequencia = a.nr_seq_ordem
				and	coalesce(b.dt_fim_preparo::text, '') = ''
				and	coalesce(b.dt_entrega_setor::text, '') = ''
				and	coalesce(b.dt_devolucao::text, '') = ''
				and	b.ie_cancelada <> 'S'
				and	c.nr_sequencia = b.nr_seq_etapa_prod
				and	a.nr_seq_lote_fornec = nr_seq_lote_p
				and	exists (	SELECT	1
						from	local_estoque loc,
							far_cabine_seg_biol x
						where	loc.cd_local_estoque = x.cd_local_estoque
						and	loc.cd_estabelecimento = cd_estabelecimento_p
						and	x.nr_sequencia = c.nr_seq_cabine);
			else
				select	coalesce(sum(a.qt_dose_real),0)
				into STRICT	qt_quimioterapia_w
				from	far_etapa_producao c,
					can_ordem_prod b,
					can_ordem_prod_mat a
				where	a.cd_material in (
						SELECT	x.cd_material
						from	material x
						where	x.cd_material_estoque = cd_material_estoque_ww)
				and	b.nr_sequencia = a.nr_seq_ordem
				and	coalesce(b.dt_fim_preparo::text, '') = ''
				and	coalesce(b.dt_entrega_setor::text, '') = ''
				and	coalesce(b.dt_devolucao::text, '') = ''
				and	b.ie_cancelada <> 'S'
				and	c.nr_sequencia = b.nr_seq_etapa_prod
				and	exists (	SELECT	1
						from	local_estoque loc,
							far_cabine_seg_biol x
						where	loc.cd_local_estoque = x.cd_local_estoque
						and	loc.cd_estabelecimento = cd_estabelecimento_p
						and	x.nr_sequencia = c.nr_seq_cabine);
			end if;
		end if;
		qt_quimioterapia_w	:= dividir(coalesce(qt_quimioterapia_w,0) , coalesce(current_setting('obter_saldo_disp_estoque_pck.qt_conv_estoque_w')::material.qt_conv_estoque_consumo%type,1));
	end if;

	return qt_quimioterapia_w;
	end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
