CREATE OR REPLACE FUNCTION obter_saldo_disp_estoque_pck.get_qt_req_trans ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_local_estoque_p bigint default null, nr_seq_lote_p bigint default null) RETURNS bigint AS $body$
DECLARE


	qt_requisicao_w			double precision	:= 0;
	cd_material_estoque_ww		material.cd_material%type;

	
BEGIN
	CALL CALL CALL CALL CALL CALL CALL CALL CALL obter_saldo_disp_estoque_pck.set_cd_estabelecimento(cd_estabelecimento_p);
	CALL obter_saldo_disp_estoque_pck.set_nr_seq_lote(nr_seq_lote_p);
	CALL obter_saldo_disp_estoque_pck.set_cd_material(cd_material_p);
	cd_material_estoque_ww		:=	obter_saldo_disp_estoque_pck.get_cd_material_estoque();

	if (current_setting('obter_saldo_disp_estoque_pck.ie_disp_req_trans_w')::parametro_estoque.ie_disp_req_trans%type = 'S') then
		if (cd_local_estoque_p > 0) then
			if	((nr_seq_lote_p > 0) and (current_setting('obter_saldo_disp_estoque_pck.ie_estoque_lote_w')::material_estab.ie_estoque_lote%type = 'S')) then
				select	coalesce(sum(a.qt_estoque),0)
				into STRICT	qt_requisicao_w
				from	operacao_estoque o,
					requisicao_material b,
					item_requisicao_material a
				where	a.nr_requisicao		= b.nr_requisicao
				and	b.cd_operacao_estoque	= o.cd_operacao_estoque
				and	b.cd_estabelecimento	= cd_estabelecimento_p
				and	b.cd_local_estoque	= current_setting('obter_saldo_disp_estoque_pck.cd_local_estoque_w')::local_estoque.cd_local_estoque%type
				and	o.ie_tipo_requisicao	in ('2','21')
				and	exists (	SELECT	1
						from	sup_motivo_baixa_req d
						where	a.cd_motivo_baixa = d.nr_sequencia
						and	d.cd_motivo_baixa = 0)
				and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
				and	a.nr_seq_lote_fornec 	= nr_seq_lote_p
				and	a.cd_material in (
					SELECT	x.cd_material
					from	material x
					where	x.cd_material_estoque = cd_material_estoque_ww);
			else
				select	coalesce(sum(a.qt_estoque),0)
				into STRICT	qt_requisicao_w
				from	operacao_estoque o,
					requisicao_material b,
					item_requisicao_material a
				where	a.nr_requisicao		= b.nr_requisicao
				and	b.cd_operacao_estoque	= o.cd_operacao_estoque
				and	b.cd_estabelecimento	= cd_estabelecimento_p
				and	b.cd_local_estoque	= current_setting('obter_saldo_disp_estoque_pck.cd_local_estoque_w')::local_estoque.cd_local_estoque%type
				and	o.ie_tipo_requisicao	in ('2','21')
				and	exists (	SELECT	1
						from	sup_motivo_baixa_req d
						where	a.cd_motivo_baixa = d.nr_sequencia
						and	d.cd_motivo_baixa = 0)
				and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
				and	a.cd_material in (
					SELECT	x.cd_material
					from	material x
					where	x.cd_material_estoque = cd_material_estoque_ww);
			end if;
		else
			if	((nr_seq_lote_p > 0) and (current_setting('obter_saldo_disp_estoque_pck.ie_estoque_lote_w')::material_estab.ie_estoque_lote%type = 'S')) then
				select	coalesce(sum(a.qt_estoque),0)
				into STRICT	qt_requisicao_w
				from	operacao_estoque o,
					requisicao_material b,
					item_requisicao_material a
				where	a.nr_requisicao		= b.nr_requisicao
				and	b.cd_operacao_estoque	= o.cd_operacao_estoque
				and	b.cd_estabelecimento	= cd_estabelecimento_p
				and	exists (	SELECT	1
						from	local_estoque loc
						where	loc.cd_local_estoque = b.cd_local_estoque
						and	loc.cd_estabelecimento = cd_estabelecimento_p)
				and	o.ie_tipo_requisicao	in ('2','21')
				and	exists (	Select	1
						from	sup_motivo_baixa_req d
						where	a.cd_motivo_baixa = d.nr_sequencia
						and	d.cd_motivo_baixa = 0)
				and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
				and	a.nr_seq_lote_fornec 	= nr_seq_lote_p
				and	a.cd_material in (
					SELECT	x.cd_material
					from	material x
					where	x.cd_material_estoque = cd_material_estoque_ww);
			else
				select	coalesce(sum(a.qt_estoque),0)
				into STRICT	qt_requisicao_w
				from	operacao_estoque o,
					requisicao_material b,
					item_requisicao_material a
				where	a.nr_requisicao		= b.nr_requisicao
				and	b.cd_operacao_estoque	= o.cd_operacao_estoque
				and	b.cd_estabelecimento	= cd_estabelecimento_p
				and	exists (	SELECT	1
						from	local_estoque loc
						where	loc.cd_local_estoque = b.cd_local_estoque
						and	loc.cd_estabelecimento = cd_estabelecimento_p)
				and	o.ie_tipo_requisicao	in ('2','21')
				and	exists (	Select	1
						from	sup_motivo_baixa_req d
						where	a.cd_motivo_baixa = d.nr_sequencia
						and	d.cd_motivo_baixa = 0)
				and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
				and	a.cd_material in (
					SELECT	x.cd_material
					from	material x
					where	x.cd_material_estoque = cd_material_estoque_ww);
			end if;
		end if;
	end if;

	return qt_requisicao_w;
	end;


$body$
