CREATE OR REPLACE FUNCTION obter_saldo_disp_estoque_pck.get_qt_sep_gedipa ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_local_estoque_p bigint default null, nr_seq_lote_p bigint default null) RETURNS bigint AS $body$
DECLARE


	qt_material_gedipa_w		double precision	:= 0;
	cd_material_estoque_ww		material.cd_material%type;

	
BEGIN
	CALL CALL CALL CALL CALL CALL CALL CALL CALL obter_saldo_disp_estoque_pck.set_cd_estabelecimento(cd_estabelecimento_p);
	CALL obter_saldo_disp_estoque_pck.set_nr_seq_lote(nr_seq_lote_p);
	CALL obter_saldo_disp_estoque_pck.set_cd_material(cd_material_p);
	cd_material_estoque_ww		:=	obter_saldo_disp_estoque_pck.get_cd_material_estoque();

	/*Busca as quantidades que foram separadas no GEDIPA, porém que serão cobradas apenas na checagem do ADEP*/

	if (current_setting('obter_saldo_disp_estoque_pck.ie_disp_sep_gedipa_w')::parametro_estoque.ie_disp_sep_gedipa%type = 'S') then
		if (cd_local_estoque_p > 0) then
			if	((nr_seq_lote_p > 0) and (current_setting('obter_saldo_disp_estoque_pck.ie_estoque_lote_w')::material_estab.ie_estoque_lote%type = 'S')) then
				/* Alteração realizada por Daniel e Hudson */

				select	coalesce(sum(qt_material),0)
				into STRICT	qt_material_gedipa_w
				from	adep_processo_item a,
					adep_processo d
				where	a.nr_seq_processo = d.nr_sequencia
				and	coalesce(a.ie_nao_requisitado,'N') <> 'S'
				and	a.ie_lanca_conta_adep = 'S'
				and	a.cd_material = cd_material_estoque_ww
				and	coalesce(d.dt_cancelamento::text, '') = ''
				and	d.cd_local_estoque = cd_local_estoque_p
				and	a.nr_seq_lote_fornec = nr_seq_lote_p
				and	d.ie_status_processo <> 'A'
				and	coalesce(d.dt_leitura::text, '') = ''
				and	d.dt_processo > clock_timestamp() - interval '2 days';
			else
				/* Alteração realizada por Daniel e Hudson */

				select	coalesce(sum(qt_material),0)
				into STRICT	qt_material_gedipa_w
				from	adep_processo_item a,
					adep_processo d
				where	a.nr_seq_processo = d.nr_sequencia
				and	coalesce(a.ie_nao_requisitado,'N') <> 'S'
				and	a.ie_lanca_conta_adep = 'S'
				and	a.cd_material = cd_material_estoque_ww
				and	coalesce(d.dt_cancelamento::text, '') = ''
				and	d.cd_local_estoque = cd_local_estoque_p
				and	d.ie_status_processo <> 'A'
				and	coalesce(d.dt_leitura::text, '') = ''
				and	d.dt_processo > clock_timestamp() - interval '2 days';
			end if;
		else
			if	((nr_seq_lote_p > 0) and (current_setting('obter_saldo_disp_estoque_pck.ie_estoque_lote_w')::material_estab.ie_estoque_lote%type = 'S')) then
				/* Alteração realizada por Daniel e Hudson */

				select	coalesce(sum(qt_material),0)
				into STRICT	qt_material_gedipa_w
				from	adep_processo_item a,
					adep_processo d
				where	a.nr_seq_processo = d.nr_sequencia
				and	coalesce(a.ie_nao_requisitado,'N') <> 'S'
				and	a.ie_lanca_conta_adep = 'S'
				and	a.cd_material = cd_material_estoque_ww
				and	coalesce(d.dt_cancelamento::text, '') = ''
				and	exists (	SELECT	1
						from	local_estoque loc
						where	loc.cd_local_estoque = d.cd_local_estoque
						and	loc.cd_estabelecimento = cd_estabelecimento_p)
				and	a.nr_seq_lote_fornec = nr_seq_lote_p
				and	d.ie_status_processo <> 'A'
				and	coalesce(d.dt_leitura::text, '') = ''
				and	d.dt_processo > clock_timestamp() - interval '2 days';
			else
				/* Alteração realizada por Daniel e Hudson */

				select	coalesce(sum(qt_material),0)
				into STRICT	qt_material_gedipa_w
				from	adep_processo_item a,
					adep_processo d
				where	a.nr_seq_processo = d.nr_sequencia
				and	coalesce(a.ie_nao_requisitado,'N') <> 'S'
				and	a.ie_lanca_conta_adep = 'S'
				and	a.cd_material = cd_material_estoque_ww
				and	coalesce(d.dt_cancelamento::text, '') = ''
				and	exists (	SELECT	1
						from	local_estoque loc
						where	loc.cd_local_estoque = d.cd_local_estoque
						and	loc.cd_estabelecimento = cd_estabelecimento_p)
				and	d.ie_status_processo <> 'A'
				and	coalesce(d.dt_leitura::text, '') = ''
				and	d.dt_processo > clock_timestamp() - interval '2 days';
			end if;
		end if;
	end if;

	return dividir(qt_material_gedipa_w, current_setting('obter_saldo_disp_estoque_pck.qt_conv_estoque_w')::material.qt_conv_estoque_consumo%type);
	end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
