CREATE OR REPLACE PROCEDURE obter_saldo_disp_estoque_pck.set_cd_material_estoque ( cd_material_p bigint) AS $body$
BEGIN
	begin
	select	cd_material_estoque,
		qt_conv_estoque_consumo
	into STRICT	current_setting('obter_saldo_disp_estoque_pck.cd_material_estoque_w')::material.cd_material%type,
		current_setting('obter_saldo_disp_estoque_pck.qt_conv_estoque_w')::material.qt_conv_estoque_consumo%type
	from	material
	where	cd_material = cd_material_p;
	exception
	when others then
		PERFORM set_config('obter_saldo_disp_estoque_pck.qt_conv_estoque_w', 1, false);
	end;

	begin
	select	coalesce(ie_estoque_lote,'N')
	into STRICT	current_setting('obter_saldo_disp_estoque_pck.ie_estoque_lote_w')::material_estab.ie_estoque_lote%type
	from	material_estab
	where	cd_material = current_setting('obter_saldo_disp_estoque_pck.cd_material_estoque_w')::material.cd_material%type
	and	cd_estabelecimento = current_setting('obter_saldo_disp_estoque_pck.cd_estabelecimento_w')::estabelecimento.cd_estabelecimento%type;
	exception
	when others then
		PERFORM set_config('obter_saldo_disp_estoque_pck.ie_estoque_lote_w', 'N', false);
	end;

	end;


$body$
