CREATE OR REPLACE FUNCTION obter_valores_aval_ped_md_pck.obter_altura_estimada_md (ie_sexo_p text, qt_idade_p bigint, qt_altura_joelho_p bigint ) RETURNS bigint AS $body$
DECLARE

		qt_altura_estimada_w bigint;
	
BEGIN
		if (ie_sexo_p = 'M') then
			qt_altura_estimada_w	:= 	(64.19 - (0.04 * qt_idade_p) + (2.02 * qt_altura_joelho_p));
		else
			qt_altura_estimada_w	:= 	(84.88 - (0.24 * qt_idade_p) + (1.83 * qt_altura_joelho_p));
		end if;

		return qt_altura_estimada_w;
	end;


