CREATE OR REPLACE PROCEDURE panorama_leito_pck.gerar_w_ocor_exame_lab_res_10 ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, pIeConcluido text) AS $body$
DECLARE


	ie_visualizacao_icon_w	varchar(1);
	ds_alerta_w	varchar(10000);
    dt_alerta_w		varchar(4000)	:= null;

	c_prescricao CURSOR FOR
	SELECT  d.dt_resultado,
			d.nr_prescricao,
			substr(obter_desc_exame_lab(e.nr_seq_exame,null,null,null),1,255) ds_exame,
			substr(obter_nome_medico(d.cd_medico,'N'),1,60) nm_medico
	from 	exame_lab_resultado d,
			exame_lab_result_item e
	where	d.nr_seq_resultado = e.nr_seq_resultado
	and		d.nr_atendimento = nr_atendimento_p
	and		e.dt_liberacao > clock_timestamp() - interval '2 days'
	and		Obter_se_prescr_vigente(d.nr_prescricao) = 'S'
	and		coalesce(e.dt_visualizacao::text, '') = ''
	and		ie_visualizacao_icon_w =  'L'
	
union all

	SELECT  d.dt_resultado,
			d.nr_prescricao,
			substr(obter_desc_exame_lab(e.nr_seq_exame,null,null,null),1,255) ds_exame,
			substr(obter_nome_medico(d.cd_medico,'N'),1,60) nm_medico
	from 	exame_lab_resultado d,
			exame_lab_result_item e
	where	d.nr_seq_resultado = e.nr_seq_resultado
	and		d.nr_atendimento = nr_atendimento_p
	and		e.dt_aprovacao > clock_timestamp() - interval '2 days'
	and		Obter_se_prescr_vigente(d.nr_prescricao) = 'S'
	and		coalesce(e.dt_visualizacao::text, '') = ''
	and		coalesce(ie_visualizacao_icon_w,'X') <> 'L';



	
BEGIN
	begin
		obter_param_usuario(281, 176, null/*cd_perfil_p*/
, '', cd_estabelecimento_p, ie_visualizacao_icon_w);
	exception
	when others then
		ie_visualizacao_icon_w := 'L';
	end;

	for r_c_prescricao in c_prescricao loop
        dt_alerta_w := 'flagStart' || to_char(r_c_prescricao.dt_resultado,'dd/mm/yyyy hh24:mi:ss') || 'flagEnd';
		ds_alerta_w	:= 	substr(ds_alerta_w || r_c_prescricao.nr_prescricao || ' - ' ||
					dt_alerta_w || ' - ' || r_c_prescricao.ds_exame || ' - ' ||
					r_c_prescricao.nm_medico || '<br>',1,4000);
	end loop;

	if (ds_alerta_w IS NOT NULL AND ds_alerta_w::text <> '') then
		CALL panorama_leito_pck.insert_ocorrencia(nr_atendimento_p,
					10,
					'assets/icons/status-and-feedback/microscope-check-grey.svg',
					'assets/icons/status-and-feedback/microscope-check.svg',
					308471, /* Resultados dos exames laboratoriais */
					967901,
					null,
					966278,
					substr(ds_alerta_w,1,4000),
					8,
                    pIeConcluido);
	end if;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
