CREATE OR REPLACE PROCEDURE panorama_leito_pck.gerar_w_ocor_exame_nonlab_57 ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, pIeConcluido text) AS $body$
DECLARE


	ds_alerta_w		varchar(10000)	:= null;
    dt_alerta_w		varchar(4000)	:= null;

	c_prescricao CURSOR FOR
	SELECT	pm.dt_prescricao,
			pm.nr_prescricao,
			p.ds_procedimento
	from	procedimento p,
			prescr_medica pm,
			prescr_procedimento pp left outer join proc_interno w on w.nr_sequencia = pp.nr_seq_proc_interno
	where	pm.nr_atendimento = nr_atendimento_p
	and		pm.nr_prescricao = pp.nr_prescricao
	and		p.cd_procedimento = pp.cd_procedimento
	and		p.ie_origem_proced = pp.ie_origem_proced
	and		pm.dt_prescricao > clock_timestamp() - interval '2 days'
	and		pp.ie_suspenso = 'N'
    and		pp.nr_seq_exame is  null
	and	((coalesce(w.nr_sequencia::text, '') = ''
			and coalesce(pp.nr_seq_proc_interno::text, '') = '') or ((w.nr_sequencia IS NOT NULL AND w.nr_sequencia::text <> '')
			and	(pp.nr_seq_proc_interno IS NOT NULL AND pp.nr_seq_proc_interno::text <> '')
			and	coalesce(pp.nr_seq_prot_glic::text, '') = ''
			and	coalesce(pp.nr_seq_origem::text, '') = ''
			and	coalesce(w.ie_tipo,'O') not in ('G','BS')
			and	coalesce(w.ie_ivc,'N') <> 'S'))
	and		exists (	SELECT	1
					from	prescr_proc_hor d
					where	pp.nr_prescricao = d.nr_prescricao
					and	pp.nr_sequencia = d.nr_seq_procedimento
					and	coalesce(d.dt_fim_horario::text, '') = ''
					and	coalesce(d.ie_situacao,'A') = 'A'  LIMIT 1)
	and		adep_obter_regra_inclusao('PROC', pm.cd_estabelecimento, pm.cd_setor_atendimento,
									pm.cd_perfil_ativo, null, pp.cd_procedimento, pp.ie_origem_proced,
									pp.nr_seq_proc_interno, pm.cd_Setor_atendimento,pp.cd_Setor_atendimento,
									pm.nr_prescricao, pp.nr_seq_exame) = 'S';



	
BEGIN

	for	r_c_prescricao in c_prescricao loop
        dt_alerta_w := 'flagStart' || to_char(r_c_prescricao.dt_prescricao,'dd/mm/yyyy hh24:mi:ss') || 'flagEnd';
		ds_alerta_w	:= 	substr(ds_alerta_w || dt_alerta_w || ' - ' || r_c_prescricao.ds_procedimento|| '<br>',1,4000);
	end loop;

	if (ds_alerta_w IS NOT NULL AND ds_alerta_w::text <> '') then
		CALL panorama_leito_pck.insert_ocorrencia(nr_atendimento_p,
						57,
						'assets/icons/status-and-feedback/image-examination.svg',
						'assets/icons/status-and-feedback/image-examination.svg',
						289737, /*Imaging/diagnostic services*/
						1127818,
						null,
						966278,
						substr(ds_alerta_w,1,4000),
						9,
                        pIeConcluido);
	end if;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
