CREATE OR REPLACE PROCEDURE panorama_leito_pck.gerar_w_ocor_it_n_conf_enf_14 ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, pIeConcluido text) AS $body$
DECLARE


	ds_alerta_w		varchar(10000)	:= null;

	
BEGIN

	ds_alerta_w	:= cpoe_item_pend_lib_nur_enf(nr_atendimento_p, 'S', 'E', 'S');

	if (ds_alerta_w IS NOT NULL AND ds_alerta_w::text <> '') then
		CALL panorama_leito_pck.insert_ocorrencia(nr_atendimento_p,
					14,
					'assets/icons/status-and-feedback/nurse-warning.svg',
					'assets/icons/status-and-feedback/nurse-warning.svg',
					654185, /* Item nao confirmado enfermagem */
					967890,
					null,
					1043295,
					ds_alerta_w,
					10,
          pIeConcluido);
	end if;

	ds_alerta_w := null;

	ds_alerta_w	:= cpoe_item_pend_lib_nur_enf(nr_atendimento_p, 'N', 'E', 'S');

	if (ds_alerta_w IS NOT NULL AND ds_alerta_w::text <> '') then
		CALL panorama_leito_pck.insert_ocorrencia(nr_atendimento_p,
					14,
					'assets/icons/status-and-feedback/nurse-interrogation.svg',
					'assets/icons/status-and-feedback/nurse-interrogation.svg',
					654185, /* Item nao confirmado enfermagem */
					967890,
					null,
					1043295,
					ds_alerta_w,
					10,
          pIeConcluido);
	end if;

	END;

$body$
