CREATE OR REPLACE PROCEDURE panorama_leito_pck.gerar_w_ocor_it_n_conf_farm_15 ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, pIeConcluido text) AS $body$
DECLARE


	ds_alerta_w		varchar(10000)	:= null;

	c_prescricao CURSOR FOR
	SELECT	a.*
	from	pan_item_prescr_nao_conf_v a
	where	a.nr_atendimento	= nr_atendimento_p
	and		coalesce(a.dt_liberacao_farmacia::text, '') = ''
	and		a.ie_momento_lote = 'F';

	
BEGIN

	for	r_c_prescricao in c_prescricao loop
		ds_alerta_w	:= 	substr(ds_alerta_w || r_c_prescricao.nr_prescricao || ' - ' || r_c_prescricao.ds_item || '<br>',1,4000);
	end loop;

	if (ds_alerta_w IS NOT NULL AND ds_alerta_w::text <> '') then
		CALL panorama_leito_pck.insert_ocorrencia(nr_atendimento_p,
					15,
					'assets/icons/status-and-feedback/medicine-interrogation.svg',
					'assets/icons/status-and-feedback/medicine-interrogation.svg',
					654193, /* Item nao confirmado farmacia */
					967860,
					null,
					1043295,
					ds_alerta_w,
					11,
                    pIeConcluido);
	end if;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
