CREATE OR REPLACE PROCEDURE panorama_leito_pck.gerar_w_ocor_it_susp_sol_6 ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, pIeConcluido text) AS $body$
DECLARE


	ds_alerta_w	varchar(10000)	:= null;
        dt_alerta_w	varchar(4000)	:= null;

	c_prescricao CURSOR FOR
	SELECT	b.nr_prescricao,
			c.dt_suspensao,
			c.nr_seq_solucao cd_item,
			substr(coalesce(c.ds_solucao,obter_prim_comp_sol(b.nr_prescricao,c.nr_seq_solucao)),1,240) ds_item
	from	atendimento_paciente a,
			prescr_medica b,
			prescr_solucao c
	where	a.nr_atendimento = nr_atendimento_p
	and		a.nr_atendimento = b.nr_atendimento
	and		c.nr_prescricao = b.nr_prescricao
	and		b.dt_prescricao > clock_timestamp() - interval '2 days'
	and		c.ie_suspenso = 'S'
	and		Obter_se_prescr_vigente(b.nr_prescricao) = 'S'
	and		(c.dt_suspensao > coalesce((	SELECT	max(x.dt_atualizacao)
						from	panorama_icone_click x
						where	x.nr_atendimento = nr_atendimento_p
						and	x.cd_icone_panorama = 6),
						c.dt_suspensao-1));

	
BEGIN

	for r_c_prescricao in c_prescricao loop
        dt_alerta_w := 'flagStart' || to_char(r_c_prescricao.dt_suspensao,'dd/mm/yyyy hh24:mi:ss') || 'flagEnd';
	ds_alerta_w	:= 	substr(ds_alerta_w || dt_alerta_w || ' - ' || r_c_prescricao.ds_item|| '<br>',1,4000);
	end loop;

	if (ds_alerta_w IS NOT NULL AND ds_alerta_w::text <> '') then
		CALL panorama_leito_pck.insert_ocorrencia(nr_atendimento_p,
						6,
						'assets/icons/status-and-feedback/cancel-grey.svg',
						'assets/icons/status-and-feedback/cancel.svg',
						654179, /* Item suspenso solucao*/
						988848,
						null,
						988845,
						ds_alerta_w,
						13,
                        pIeConcluido);
	end if;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
