CREATE OR REPLACE PROCEDURE panorama_leito_pck.gerar_w_ocor_pendencias_20 ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, pIeConcluido text) AS $body$
DECLARE

        ds_alerta_w	     varchar(10000) := null;
        w_tot_pendencias bigint := 0;
        w_achou_atrasado bigint := 0;
	
BEGIN
        SELECT  count(1)
        INTO STRICT    w_achou_atrasado
        FROM    w_pan_ocorrencia
        WHERE   nr_atendimento = nr_atendimento_p
        AND     ie_icone = 21;

        if (w_achou_atrasado = 0) then
	   SELECT  count(1) as tot_pendencias
	   INTO STRICT    w_tot_pendencias
	   FROM    wl_worklist wl
	   RIGHT JOIN wl_item it ON wl.nr_seq_item = it.nr_sequencia
	   INNER JOIN wl_regra_worklist rw ON rw.nr_seq_item = it.nr_sequencia
	   WHERE    it.ie_situacao = 'A'
	   AND      wl.dt_inicial  BETWEEN TRUNC(clock_timestamp() - interval '7 days') AND TRUNC(clock_timestamp() + interval '1 days')
	   AND     wl.cd_pessoa_fisica = cd_pessoa_fisica_p
	   AND     obter_status_worklist(wl.nr_sequencia , 'C') = 'T'
	   ORDER   BY SUBSTR(obter_prioridade_worklist(wl.nr_seq_item),1,80);


            if (w_tot_pendencias > 0) then
            	ds_alerta_w:= '#@1015753#@';
            end if;

            if (ds_alerta_w IS NOT NULL AND ds_alerta_w::text <> '') then
                CALL panorama_leito_pck.insert_ocorrencia(nr_atendimento_p,
                                  20,
                                  'assets//icons/status-and-feedback/clock.svg',
                                  'assets//icons/status-and-feedback/clock.svg',
                                  755039,   /* Patient's Pending Items */
                                  1015777,  /* Pendencies on the work list */
                                  null,
                                  1015705,  /* Worklist */
                                  ds_alerta_w,
                                  1,
                                  pIeConcluido);
            end if;
        end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
