CREATE OR REPLACE PROCEDURE panorama_leito_pck.gerar_w_ocor_pendencias_21 ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, pIeConcluido text) AS $body$
DECLARE

        ds_alerta_w	     varchar(10000) := null;
        w_tot_pendencias bigint := 0;
	w_tot_pendencias_atrasadas bigint := 0;
	
BEGIN

	   SELECT  count(1) as tot_pendencias
	   INTO STRICT    w_tot_pendencias_atrasadas
	   FROM    wl_worklist wl
	   RIGHT JOIN wl_item it ON wl.nr_seq_item = it.nr_sequencia
	   INNER JOIN wl_regra_worklist rw ON rw.nr_seq_item = it.nr_sequencia
	   WHERE    it.ie_situacao = 'A'
	   AND      wl.dt_inicial  BETWEEN TRUNC(clock_timestamp() - interval '7 days') AND TRUNC(clock_timestamp() + interval '1 days')
	   AND     wl.cd_pessoa_fisica = cd_pessoa_fisica_p
	   AND     obter_status_worklist(wl.nr_sequencia , 'C') = 'A'
	   ORDER   BY SUBSTR(obter_prioridade_worklist(wl.nr_seq_item),1,80);

	if (w_tot_pendencias_atrasadas > 0) then
	   SELECT  count(1) as tot_pendencias
	   INTO STRICT    w_tot_pendencias
	   FROM    wl_worklist wl
	   RIGHT JOIN wl_item it ON wl.nr_seq_item = it.nr_sequencia
	   INNER JOIN wl_regra_worklist rw ON rw.nr_seq_item = it.nr_sequencia
	   WHERE    it.ie_situacao = 'A'
	   AND      wl.dt_inicial  BETWEEN TRUNC(clock_timestamp() - interval '7 days') AND TRUNC(clock_timestamp() + interval '1 days')
	   AND     wl.cd_pessoa_fisica = cd_pessoa_fisica_p
	   AND     obter_status_worklist(wl.nr_sequencia , 'C') = 'T'
	   ORDER   BY SUBSTR(obter_prioridade_worklist(wl.nr_seq_item),1,80);

		w_tot_pendencias := w_tot_pendencias + w_tot_pendencias_atrasadas;
		ds_alerta_w:= '#@1015844#@';
        end if;

        if (ds_alerta_w IS NOT NULL AND ds_alerta_w::text <> '') then
            CALL panorama_leito_pck.insert_ocorrencia(nr_atendimento_p,
                              21,
                              'assets//icons/status-and-feedback/clock-yellow.svg',
                              'assets//icons/status-and-feedback/clock-yellow.svg',
                              755039,   /* Pendencias do Paciente */
                              1015841,  /* Overdue pendencies on the work list */
                              null,
                              1015705,  /* Worklist */
                              ds_alerta_w,
                              17,
                              pIeConcluido);
        end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
