CREATE OR REPLACE PROCEDURE panorama_leito_pck.gerar_w_ocor_res_critico_11 ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, pIeConcluido text) AS $body$
DECLARE


	ds_alerta_w					varchar(10000)	:= null;
	dt_alerta_w		varchar(4000)	:= null;
	ie_visualizacao_icon_w		varchar(3);

	c_exame CURSOR FOR
	SELECT  d.dt_resultado,
			d.nr_prescricao,
			substr(obter_desc_exame_lab(e.nr_seq_exame,null,null,null),1,255) ds_exame,
			substr(obter_nome_medico(coalesce(d.cd_medico,e.cd_medico_resp),'N'),1,60) nm_medico,
			substr(obter_result_lab_panorama(d.nr_seq_resultado, e.ds_resultado, e.qt_resultado, e.pr_resultado, e.nr_seq_prescr, d.nr_prescricao, e.nr_seq_exame),1,255) ||
				' ' || Obter_LAB_Unid_Med(e.nr_seq_unid_med,'C') ds_resultado
	from	exame_lab_resultado d,
			exame_lab_result_item e
	where	d.nr_seq_resultado = e.nr_seq_resultado
	and		d.nr_atendimento = nr_atendimento_p
	and		coalesce(e.dt_visualizacao::text, '') = ''
	and		e.dt_liberacao > clock_timestamp() - interval '1 days' and e.dt_liberacao > coalesce((SELECT max(x.dt_atualizacao) from panorama_icone_click x where x.nr_atendimento = nr_atendimento_p and x.cd_icone_panorama = 11), e.dt_liberacao-1)
	and		lab_se_result_nao_aceit(d.nr_seq_resultado, e.ds_resultado, e.qt_resultado, e.pr_resultado, e.nr_seq_prescr, d.nr_prescricao, e.nr_seq_exame) = 'S'
	and (ie_visualizacao_icon_w =  'L')
	
union all

	select  d.dt_resultado,
			d.nr_prescricao,
			substr(obter_desc_exame_lab(e.nr_seq_exame,null,null,null),1,255) ds_exame,
			substr(obter_nome_medico(coalesce(d.cd_medico,e.cd_medico_resp),'N'),1,60) nm_medico,
			substr(obter_result_lab_panorama(d.nr_seq_resultado, e.ds_resultado, e.qt_resultado, e.pr_resultado, e.nr_seq_prescr, d.nr_prescricao, e.nr_seq_exame),1,255) ||
				' ' || Obter_LAB_Unid_Med(e.nr_seq_unid_med,'C') ds_resultado
	from	exame_lab_resultado d,
			exame_lab_result_item e
	where	d.nr_seq_resultado = e.nr_seq_resultado
	and		d.nr_atendimento = nr_atendimento_p
	and		coalesce(e.dt_visualizacao::text, '') = ''
	and		e.dt_aprovacao > clock_timestamp() - interval '1 days' and e.dt_aprovacao > coalesce((select max(x.dt_atualizacao) from panorama_icone_click x where x.nr_atendimento = nr_atendimento_p and x.cd_icone_panorama = 11), e.dt_aprovacao-1)
	and		lab_se_result_nao_aceit(d.nr_seq_resultado, e.ds_resultado, e.qt_resultado, e.pr_resultado, e.nr_seq_prescr, d.nr_prescricao, e.nr_seq_exame) = 'S'
	and (coalesce(ie_visualizacao_icon_w,'X') <> 'L');

	
BEGIN

	begin
		obter_param_usuario(281, 176, null/*cd_perfil_p*/
, '', cd_estabelecimento_p, ie_visualizacao_icon_w);
	exception
	when others then
		ie_visualizacao_icon_w := 'L';
	end;

	for r_c_exame in c_exame loop
        dt_alerta_w := 'flagStart' || to_char(r_c_exame.dt_resultado,'dd/mm/yyyy hh24:mi:ss')  || 'flagEnd';
		ds_alerta_w	:= 	substr(ds_alerta_w || r_c_exame.nr_prescricao || ' - ' || dt_alerta_w ||' - ' ||
								r_c_exame.ds_exame || ' - ' || trim(both r_c_exame.ds_resultado) || ' - ' || r_c_exame.nm_medico || '<br>',1,4000);
	end loop;

	if (ds_alerta_w IS NOT NULL AND ds_alerta_w::text <> '') then
		CALL panorama_leito_pck.insert_ocorrencia(nr_atendimento_p,
						11,
						'assets/icons/status-and-feedback/circle-grey-flag.svg',
						'assets/icons/status-and-feedback/circle-red-flag.svg',
						654182, /* Resultado critico*/
						967511,
						null,
						966278,
						ds_alerta_w,
						2,
                        pIeConcluido);
	end if;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE panorama_leito_pck.gerar_w_ocor_res_critico_11 ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, pIeConcluido text) FROM PUBLIC;
